INSERT INTO `quest_step` VALUES ('384', 'Trouver le Sram.', 'Yse Vewybad vous a parlé d\'un Sram important en Amakna... Où trouve-t-on des Srams en Amakna?', '1000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('386', 'La crypte, c\'est chic.', 'Vous devez visiter les cinq cryptes du cimetière d\'Amakna.', '3000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('387', 'Ca roule pour les Roublards.', 'Vous avez maintenant une idée très précise de la cachette des Roublards. Reste à vérifier vos informations.', '2000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('388', 'Pour quelques Roublards de plus...', 'Vous devez vous inflitrer chez les Roublards et aller parler à Vil Smisse.', '15000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('390', 'Dilemme dilemme..', 'Vous savez maintenant qu\'Yse Vewybad complote contre le Roi. Il va falloir prendre parti pour l\'un ou l\'autre.', '2000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('202', 'Trouver le hors-la-loi', 'Musha a été aperçu au sud de Pandala. \nIl est condamné à l\'emprisonnement à Astrub. \n', '0', '64000', '0', null, '0');
INSERT INTO `quest_step` VALUES ('15', 'Le grenier hanté', '...', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('395', 'A la pêche aux infos.', 'Vil Smisse veut que vous recueillez des renseignements sur ce qui se prépare au château. Il faut donc trouver la personne qui saura vous dire ça.', '3000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('402', 'Fausse identité', 'Comme vous pouvez le voir, Uba Tessé vous a transformé en Wabbit. Il n\'y a plus qu\'à aller voir au château si vous pouvez faire capoter ce mariage.', '50000', '25000', '0', null, '0');
INSERT INTO `quest_step` VALUES ('406', 'Tueurs à gage à engager', 'Vil Smisse ne pourra utiliser le blopisier empoisonné. Il se rabat donc sur l\'attaque de front. Pour cela, il veut engager des assassins, il a donc besoin d\'argent.', '76000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('875', 'L\'éternelle moisson.', 'Vous avez la liste des créatures, reste à les trouver.', '6523500', '0', '9687,14;9689,6', null, '0');
INSERT INTO `quest_step` VALUES ('730', 'Redésosser Mak Gahan.', 'Mak Gahan le Chafer veut sa revanche.', '1000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('408', 'Sus au tofu!', 'Vous savez où le pwince d\'Wa est parti chasser, enfin à peu près... c\'est au sud de Bonta en tout cas. Reste à trouver ce fameux pwince.', '100000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('197', 'Levez-vous et marchez.', 'Elviana Tirips veut interroger les renégats bontariens mais avant de pouvoir le faire il faut les ressusciter.', '50000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('409', 'Qui m\'aime me suive!', 'Le pwince d\'Wa vous a demandé de lui constituer une suite digne de sa stature... Vu ses prouesses au combat, son régime particulier et son sens de l\'esthétisme, il faut bien quatre personnes pour l\'assister.', '45000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('414', 'Servir ou rendre service?', 'L\'intendant San Lavalss vous demande d\'aider aux derniers préparatifs du mariage.', '86000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('415', 'Au meurtre!', 'Le pwince d\'Wa se fait attaquer, protégez-le!', '5000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('416', 'Bunny suit', 'Le pwince d\'Wa vous a demandé de prendre sa place pour démasquer les assassins. Encore faut-il trouver quelqu\'un qui puisse vous tranformer en Wabbit.', '15000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('205', 'Récompense ?', 'Poochan vous attend.', '7000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('1045', 'Changer de carte.', 'Pour changer de carte, déplacez votre curseur sur un des {arrow,1280,520,0,0::bords de l\'écran} et cliquez avec le bouton gauche de votre souris lorsque le curseur se transforme en flèche.', '5', '0', '10794', null, '0');
INSERT INTO `quest_step` VALUES ('1047', 'Choisir sa position de départ.', 'Cliquez sur une des {cell,272::cases bleues} avec le bouton gauche de votre souris pour choisir votre position de départ et cliquez sur le {ui,banner,btn_ready::bouton prêt} pour commencer le combat.', '5', '0', '10796', null, '0');
INSERT INTO `quest_step` VALUES ('254', 'Un bon repas', 'Lorkos vous offrira une clef du labyrinthe si vous lui rapportez de quoi préparer un bon repas.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('1049', 'Utiliser un sort.', 'Sélectionnez {ui,banner,gd_spellitemotes,0::un sort} et cliquez avec le bouton gauche de votre souris sur une cible. Certains sorts se lancent sur les ennemis, et d\'autres sur les alliés ou sur votre personnage. La portée du sort est indiquée grâce aux cases bleues. Les sorts nécessitent des {ui,banner,lbl_pa,2::points d\'action} (PA) pour être lancés. Vous récupérez vos {ui,banner,lbl_pa,2::PA} au début de votre prochain tour.', '5', '0', '10798', null, '0');
INSERT INTO `quest_step` VALUES ('1051', 'Gagner le combat.', 'Continuez d\'attaquer votre cible pour gagner le combat. Passez votre tour grâce au bouton {ui,banner,btn_skipTurn::fin de tour} lorsque vous voulez regagner vos {ui,banner,lbl_pa,2::PA} et vos {ui,banner,lbl_pm,2::PM}.', '5', '0', '10784', null, '0');
INSERT INTO `quest_step` VALUES ('1052', 'Commencer une quête.', 'Parlez à {npc,1222::Maître Pifo} pour lancer une quête (les PNJ qui proposent des quêtes ont un point d\'exclamation au dessus de leur tête).', '5', '0', '10800', null, '0');
INSERT INTO `quest_step` VALUES ('1059', 'Conclure l\'apprentissage.', 'Pour valider votre quête, vous devez parler à {npc,1222::Maître Pifo}.', '5', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('872', 'L\'éternelle moisson.', 'Vous avez la liste des créatures, reste à les trouver.', '2800000', '0', '9687,20', null, '0');
INSERT INTO `quest_step` VALUES ('1043', 'Parler à un pnj.', 'Cliquez sur {npc,1222::Maître Pifo} avec le bouton gauche de votre souris et sélectionnez l\'action \" + \"\"\" + \"Parler\" + \"\"\" + \" pour dialoguer avec lui.', '5', '0', '10785', null, '0');
INSERT INTO `quest_step` VALUES ('1046', 'Déclencher un combat.', 'Cliquez sur {monster,2785::Epouvantail d\'Incarnam} avec le bouton gauche de votre souris pour lancer un combat.', '5', '0', '10795', null, '0');
INSERT INTO `quest_step` VALUES ('679', 'Battre le milicien.', 'Vous devez battre Tarche le milicien, dans la salle à côté.', '50', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('682', 'Les dents de Bouftou.', 'Vous devez arracher des dents de Bouftous, exactement le nombre constituant un dentier humain, et les amener à Ned, au sud-ouest de la ville.', '500', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('1079', 'Usine à jouets.', 'D\'abominables créatures ont cambriolé l\'entrepôt du Père Nowel. Vous devez récupérer les caisses de cadeaux qu\'elles ont volées, avec ou sans leur consentement, et les apporter à Félix Nowel.', '0', '1000', '10920', null, '0');
INSERT INTO `quest_step` VALUES ('685', 'Retrouver le Corbac.', 'Vous devez retrouver le Corbac qui a volé le collier de la fille du maire, le battre et récupérer le collier. Le Corbac serait vers la tour.', '2000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('689', 'Entraînement au bâton.', 'Amayiro vous a demandé vingt bâtons de boisaille pour entraîner ses troupes.', '4000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('690', 'Tabasser le mauvais payeur.', 'Le tavernier Tabasse doit une forte somme à Amayiro : vous devez le convaincre par tous les moyens de la payer.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('691', 'La réparation du katana.', 'Vous devez demander à Kosuke le forgeron de réparer la lame d\'Amayiro en échange du chèque.', '4600', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('693', 'Apporter la poudre.', 'Vous devez apporter dix pods de poudre à canon à Amayiro. Vous pourrez les acheter à Moub Reicifitra, dans Bonta.', '5800', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('694', 'Colmater la brèche.', 'Shere Hacanon, à l\'entrée de Bonta, a besoin de quatre pierres de Craqueleur des plaines pour colmater la brèche des murailles.', '6400', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('699', 'Les informations du capitaine.', 'Vous devez trouver un moyen pour entrer en communication avec feu le capitaine Tsog, afin d\'obtenir les informations qu\'il doit faire passer. Le capitaine Alrok connait l\'emplacement de la tombe de Tsog.', '9400', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('686', 'Traquer les buveurs.', 'Vous devez faire le tour des tavernes de Bonta pour trouver les miliciens qui ne sont pas à leur poste et leur demander leur badge.', '2500', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('701', 'L\'invocation inconnue.', 'Amayiro vous a demandé de vous renseigner auprès de Zeurg (maître de classe Osamodas) à propos d\'une invocation inconnue employée par Brâkmar.', '10700', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('785', 'Interro surprise.', 'Elviana Tirips veut savoir quelles informations les renégats bontariens ont données aux Brâkmariens et c\'est vous qui êtes en charge de l\'interrogatoire.', '200000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('702', 'Amener les capes.', 'Amayiro vous a demandé d\'apporter 10 capes bontariennes au capitaine Koda, à côté. Elles se confectionnent avec une teinture magique bleue, une plume de corbac et une laine de boufton blanc.', '11400', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('705', '21 heures.', 'Danathor vous a demandé de le retrouver à 21 heures avec les ingrédients indiqués sur sa liste.', '13500', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('707', 'Assassinat refusé.', 'Retournez voir Amayiro.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('717', 'Travail au choix.', 'Vous devez apporter à Amayiro : soit 20 planches en frêne, soit 10 racines d\'Abraknyde.', '21000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('718', 'Une requête de Danathor.', 'Danathor vous a envoyé une lettre pour vous demander assistance.', '11500', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('719', 'Larry Poller.', 'Larry Poller a demandé votre aide. Il se trouve près de la tour de Bonta.', '11500', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('725', 'Le sang des rats.', 'Amayiro vous a demandé d\'anéantir quatre Rats d\'Hyoactif dans les égoûts de Bonta.', '40000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('432', 'Chasse sur la plage', 'Crocodaille Dandi aimerait que vous éliminiez certaines créatures sur la plage de Corail.', '50000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('200', 'Brâkmariens bras cassés.', 'Les agents brâkmariens étant des incapables, Kaffra vous a demandé de vous rendre à Astrub et de convaincre le marchand bontarien Raymond Santho de ravitailler Brâkmar.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('500', 'Si près du but.', 'Norb sait où se trouve le forgeron ermite, il vous reste à le convaincre de divulguer sa cachette. Peut-être qu\'Amayiro pourra vous aider.', '122000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('782', 'Agriculture à haut risque.', 'Kaffra a eu vent des plans de Bonta pour accroître sa production céréalière. Il a besoin de 20 Graines de scorbute pour que cette production serve les desseins de Brâkmar.', '50000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('198', 'Inquiétudes brâkmariennes.', 'Ledrob se demande ce qui se trame à Bonta en ce moment. Il vous envoie sur place pour prendre contact avec l\'espion Finche Aticus.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('29', 'A la recherche du nouveau livreur', 'Trouver une personne à la fois forte et agile pour prendre la place de nouveau livreur.', '2000', '250', '0', null, '0');
INSERT INTO `quest_step` VALUES ('783', 'Intoxication alimentaire.', 'Suite à votre rapport, Ledrob vous a confié 20 Graines de scorbute modifiées à déposer dans l\'atelier des alchimistes de Bonta. Il faut les déposer de nuit lorsque Merlan l\'Enchaîneur est endormi.', '50000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('475', 'Affaire louche.', 'Vous allez profiter de l\'absence de Xarthan pour vous introduire discrètement dans sa maison et la fouiller. Les services d\'un bricoleur compétent seront nécessaires.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('787', 'J\'ai dit : Lève-toi et marche !', 'La malédiction d\'Erazal vous a appris comment forcer une âme à se réincarner contre sa volonté. Pour cela, il faut créer une potion particulière et suivre un rituel précis.', '50000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('784', 'A coeur vaillant, rien d\'impossible.', 'Vous devez trouver un membre de la famille Terra et l\'emmener auprès de Jerhyn Gholein. Setag, le maître de classe Enutrof, pourra sans doute vous aider.', '200000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('481', 'Kaffra Kyper.', 'Oto Mustam veut avoir l\'avis de Kaffra Kyper pour son offensive contre Bonta.', '110000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('786', 'La chasse aux renégats est ouverte.', 'Des renégats bontariens se sont réfugiés à Brâkmar. Vous devez les retrouver et les éliminer puis ramener leur dépouille à Bonta.', '200000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('499', 'Ainsi forgeront...', 'Il faut trouver un forgeron compétent qui accepterait de faire de l\'équipement pour Bonta.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('210', 'A coeur saignant, rien d\'impossible.', 'Vous devez vous emparer des statuettes symboles des biens et des maux de l\'humanité et les remettre à Kaffra. Elles sont cachées dans une mine abandonnée près du temple Enutrof.', '200000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('208', 'Le pont vers Terra Tibya.', 'Ledrob Terceséc veut mettre la main sur les statuettes de Sumens. Il vous a chargé de capturer leur gardien.', '200000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('442', 'La tour désordre.', 'Trouvez dans Bonta un personnage important qui serait susceptible d\'être corrompu. Oto a suggéré de chercher dans la tour.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('444', 'Les bons bars de Bonta.', 'Il faut trouver des informations sur Xephirès. Les taverniers de Bonta pourraient peut-être fournir une piste.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('445', 'Abrakadabra, me voilà.', 'Vous avez rapporté des informations utiles à Oto Mustam, qui vous a demandé de prendre la relève d\'un milicien. Vous devez chercher un Sram connaissant Xephirès, dans la forêt des Abraknydes.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('446', 'Abrakadabra, c\'est encore moi.', 'Exoram n\'est pas rassuré car il a entendu des bruits suspects et des rires narquois plus au nord. Si vous pouvez lui assurer que le chemin est dégagé, il vous donnera les informations prisées sur Xephirès.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('447', 'S\'infiltrer ou foncer dans le tas, telle est la question.', 'D\'après vos découvertes, il semblerait qu\'Exoram soit retenu prisonnier dans le campement gobelin sauf s\'il est déjà mort.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('449', 'Le repaire repéré.', 'Grâce au message du chef des voleurs vous êtes en mesure de trouver son repaire. Pourquoi ne pas y faire un tour avant de rentrer à la milice ?', '90000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('452', 'Un échec... temporaire', 'Xephirès a refusé l\'offre, mais tout n\'est pas perdu. Un bijou saura l\'amadouer.', '75000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('873', 'L\'éternelle moisson.', 'Vous avez la liste des créatures, reste à les trouver.', '3656000', '0', '9687,20', null, '0');
INSERT INTO `quest_step` VALUES ('453', 'De la bave pour confectionner un bijou ?', 'Vous devez récupérer des matériaux pour confectionner un joyau : 5 Lingots d\'Or, 1 Bave du Bouftou Royal et 1 Nacre. La Nacre s\'obtient en polissant 10 Nacres brutes que l\'on trouve sur les crustacés et mollusques de l\'île d\'Otomaï.', '80000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('456', 'Vite, vite !', 'Milone serait apparemment sur le point d\'être cambriolé. Il faut vite aller empêcher cela.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('458', 'Le facteur.', 'Il faut trouver un moyen de faire passer la lettre à Xephirès. Il est souvent à la taverne du Pandawa Ivre mais refuse désormais de vous parler.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('459', 'L\'entretien.', 'Tout est prêt, il ne reste plus qu\'à retrouver Xephirès à la Taverne du Pandawa Ivre pour lui remettre la lettre et fixer un rendez-vous pour sa réponse.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('460', 'Victoire !', 'Xephirès a accepté les conditions et est prêt à trahir Bonta le moment venu. Oto sera satisfait d\'apprendre la nouvelle.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('461', 'Renseignements à la Bagrutte', 'Le marchand de vin local a disparu. Le tavernier de la Bagrutte a peut-être des renseignements.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('464', 'Repaire pépère.', 'Amayiro vous a demandé d\'enquêter dans les plaines de Cania pour trouver le repaire des bandits. D\'après vos informations il serait non loin de la foire du trooll, un peu à l\'Ouest.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('466', 'Escorte Gueurle.', 'Votre mission est d\'escorter la troupe de marchands de Gueurle jusqu\'à Astrub.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('469', 'Mauvais hameçon ?', 'Le voyage s\'est passé sans encombre. Les bandits n\'ont pas attaqué... Amayiro attend votre rapport.', '75000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('473', 'Une bonne Rigalade', 'Rigal Main Crochue, au temple Sram, est réputé pour détenir de nombreuses informations, mais toute information à un prix. Reste à savoir à combien s\'élève la vôtre.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('474', 'Il est frais mon poison.', 'Vous devez trouver les réserves de poison de Brâkmar et dérober le registre des clients de l\'atelier des alchimistes. Bishom Pot pourra peut-être vous aider.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('275', 'Pour le titre', 'Pat va juger de vos capacités.', '45000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('38', 'Livraison de Erty Trapchett', '', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('476', 'Bishom Pot, le retour.', 'Amayiro soupçonne Brâkmar d\'être à l\'origine des problèmes de Bonta. Bishom Pot, espion milicien à Brâkmar, a peut-être appris quelque chose.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('477', 'Le vieux bijoutier.', 'Selon le chef des voleurs, le bijoutier de Brâkmar - Milone Corichel - aurait des informations.', '100000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('68', 'L\'utilité d\'une bonne poignée d\'épée', 'Trouvez une poignée afin de faire forger votre épée.', '1200', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('478', 'Attrape-nigaud.', 'Un rendez-vous entre un Brâkmarien et le Bontarien corrompu va avoir lieu à la tour de Bonta. Il faut les capturer tous les deux si possible.', '105000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('483', 'Une bien belle grosse caisse.', 'Vous devez tromper la vigilance du gardien pour lui prendre les clefs de l\'entrepôt. Il vous faut ensuite dérober une caisse d\'équipement et l\'apporter à Mulic Bere pour expertise.', '116000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('906', 'La bonne planque.', 'Vous devez trouver une nouvelle planque pour les espions entre leur emplacement actuel et la bibliothèque. Au besoin, vous devez neutraliser ses éventuels occupants.', '128000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('488', 'Retour vers Oto', 'Vous vous êtes occupé du tacticien, il faut retourner voir Oto.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('1021', 'Abattre le tacticien.', 'Vous avez décidé de recourir à la solution la plus extrême. La plus Brâkmarienne en somme.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('489', 'Retour vers Oto.', 'Le tacticien ayant quitté la ville, vous pouvez raisonnablement laisser penser à Oto Mustam que vous l\'avez tué.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('908', 'Dompteur de bêtes, mais aussi d\'animaux.', 'Oto Mustam veut connaître le secret du domptage des crocs glands. Vous devez retourner à la bibliothèque de Bonta pour y trouver des indices.', '134000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('491', 'Nouveau danger qui fait peur.', 'Plusieurs guerriers brâkmariens ont été vaincus et laissés pour morts sur la route menant à Astrub. Il faut aller enquêter là-bas mais avant, Oto Mustam veut une âme de Croc Gland.', '140000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('492', 'Marteau et enclume.', 'Oto Mustam pense que Bonta est aidée par un forgeron mercenaire. Vous devez chercher dans les ateliers de forgerons ou de mineurs pour trouver des indices.', '146000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('494', 'Interception brutale.', 'Vous devez intercepter un convoi d\'armes qui va passer par l\'orée de la Forêt des Abraknydes pour rejoindre le Village des Brigandins. Des renforts seront les bienvenus.', '158000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('495', 'Pirate à quai.', 'Grâce à Xephirès, vous savez que le navire bontarien La Descemer transporte de nombreux objets magiques dans ses cales. Vous devez le trouver et faire main-basse sur sa marchandise.', '164000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('496', 'Escarmouche.', 'Un combat a actuellement lieu près de la Baie de Cania, sur la route à mi-chemin entre Bonta et Brâkmar. Il faut le rejoindre et aider Bonta à vaincre !', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('498', 'Forger avec ses pieds.', 'Vous devez enquêter sur le fournisseur de l\'équipement de la milice et mettre à l\'épreuve le forgeron qui le fabrique.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('874', 'L\'éternelle moisson.', 'Vous avez la liste des créatures, reste à les trouver.', '4706000', '0', '9687,20', null, '0');
INSERT INTO `quest_step` VALUES ('501', 'Prospection immobilière.', 'Nara vous a demandé de trouver un endroit isolé où installer sa forge et son matériel.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('928', 'Pretty Nara.', 'Nara vous a fournit la longue liste de fournitures dont il a besoin pour forger l\'équipement de la milice de Bonta. Vous devez les lui amener dans la Mine des Porcos.', '128000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('955', 'Agence immobilière.', 'Vous avez visité les différents lieux indiqués par Setag. Reste à convaincre Nara de déménager dans l\'un d\'eux.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('935', 'Kokoboy.', 'Mieux vaut être prévenant et acquérir un stock de Noix de Kokoko conséquent avant de retourner auprès d\'Amayiro.', '170000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('503', 'Matières premières.', 'Nara le forgeron, dans la mine du territoire Porcos, a besoin de matières premières pour forger un équipement standard destiné aux miliciens de Bonta.', '140000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('425', 'Une potion pour le professeur', 'Le Professeur Fournesol a besoin d\'une Potion Bulbique pour se remettre au travail.', '30000', '1000', '0', null, '0');
INSERT INTO `quest_step` VALUES ('930', 'Bwork, quelle odeur !', 'Soit Epuop Tik n\'en sait pas plus, soit il refuse de vous en dire davantage. Quoiqu\'il en soit, les bworks mages devraient être moins réticents à vous informer sur l\'un des leurs.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('931', 'Génialissime.', 'Si vous levez la malédiction pesant sur Jibworknouille, il vous divulguera sûrement ce qu\'il sait en signe de reconnaissance. Il a suggéré de se renseigner dans une bibliothèque.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('508', 'Sadikoi ?', 'Danathor a encore besoin de vos services pour mettre la main sur le grimoire du bwork mage des cavernes réputé pour avoir lancé l\'envoûtement de la Grafioze.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('933', 'Sadidarne.', 'Touh Flowa vous a indiqué où se trouve l\'entrée de la prison végétale. Comme il avait l\'esprit quelque peu embrumé, il faut espérer qu\'il n\'a rien omis de vous dire.', '158000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('509', 'Vol dans un nid de coucou.', 'Amayiro veut tendre une embuscade aux Brâkmariens pour tester le nouveau matériel et la cohésion des miliciens.', '152000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('510', 'Plan d\'attaque.', 'Amayiro craint un assaut imminent des Brâkmariens. Vous devez trouver leur plan d\'attaque pour permettre à Amayiro de leur préparer un comité d\'accueil digne de ce nom.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('513', 'Carapaces de tortue.', 'Le tavernier du Ripate veut que vous lui apportiez des carapaces de tortue de Moon, une de chaque couleur, mais peut-être qu\'une grosse somme de Kamas pourrait les remplacer.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('934', 'Une corne à la mer.', 'Selon le Tavernier du Ripate la corne se trouverait sur une île. Des érudits pourront sans doute vous renseigner davantage.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('515', 'Amadoudoudou c\'est bon.', 'Il faut trouver un moyen d\'amadouer Turorte pour mettre la main sur la Corne Edfrit. Les résidents de l\'île de Moon peuvent vous aider.', '184000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('858', 'L\'éternelle moisson.', 'Vous avez la liste des créatures, reste à les trouver.', '2570000', '0', '9688,20', null, '0');
INSERT INTO `quest_step` VALUES ('516', 'Lenseignements auplès des Lenalds.', 'Turorte a côtoyé et défendu les Lenalds par le passé. Les Lenalds, au sud de l\'île des Wabbits, s\'en souviennent peut-être encore.', '191000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('936', 'Nos amies les bêtes.', 'Amayiro souhaite en savoir plus sur Turorte. Oshimo, le gardien du Chanil, détient sûrement des informations sur cette tortue.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('520', 'Course contre la montre.', 'Un espion bontarien est en train de fuir Brâkmar par l\'Est de la route. Il faut le retrouver et l\'escorter. Le temps vous est compté !', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('544', 'Equiper les guerriers.', 'Oto craint une attaque de Bonta et vous a demandé d\'équiper certains des guerriers de Brâkmar avec de l\'équipement spécial.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('521', 'Récupération mouvementée.', 'Vous avez trouvé l\'espion à temps mais les Brâkmariens ne comptent pas vous laisser les documents. Il vous faut les amener à Amayiro coûte que coûte.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('952', 'Petite blague entre ennemis.', 'Les Brâkmariens devaient remettre les documents à leur supérieur. Vous en avez rédigés des faux pour les remplacer et entraîner vos ennemis sur une fausse piste.', '212000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('524', 'La décision de Turorte.', 'Convaincre Turorte d\'échanger le Bandana Rapiécé contre la Corne Edfrit. Vous avez besoin d\'une Fourbacoiffe et d\'une Bouteille de Gloutoburg pour qu\'elle vous apparaisse.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('525', 'Présence brâkmarienne.', 'Vous avez attaqué un Brâkmarien qui était déjà sur place mais qui ne possédait pas encore la Corne Edfrit. Si Ecaflip est de votre côté peut-être trouverez-vous la Corne Edfrit avant les Brâkmariens.', '219000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('734', 'Tabasser les Bontariens.', 'Oto vous a demandé une bière de la Tabasse. La taverne de la Tabasse se trouve à Bonta.', '3000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('938', 'La Révélation.', 'Sous l\'hôtel de vente des alchimistes il y a une armoire. Il vous suffit de vous placer devant pour ouvrir le passage secret. Le Mage Ax vous attend là-bas.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('528', 'Carapaces.', 'Le tavernier du Ripate vous a demandé une carapace de tortue de Moon de chaque couleur. Mais peut-être allez-vous opter pour une méthode plus rapide.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('910', 'Jamais mieux servi que par soi-même.', 'L\'information révélée par le Tavernier du Ripate n\'est pas exploitable telle quelle. Une recherche auprès des érudits pourrait s\'avérer plus fructueuse.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('911', 'Kokoko, koko ?', 'Pressentant la prochaine mission que vous confiera Oto Mustam, mieux vaut préparer le terrain et acquérir quelques noix de kokoko.', '170000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('529', 'Recherches pour une corne.', 'Le soi-disant gardien de la Corne Edfrit serait dans la jungle profonde de l\'île de Moon. D\'après les rumeurs, vous devez porter une Fourbacoiffe et avoir du Gloutoburg pour qu\'il apparaisse.', '177000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('530', 'Mettre la tortue sur le dos.', 'Puisque vous n\'arrivez pas à soutirer des informations juste en demandant, il va falloir y aller d\'une façon plus directe...', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('912', 'Le tort tue.', 'Turorte ayant faim et la manière forte étant un échec, il faut trouver un moyen de l\'empoisonner. Un habitant de l\'île connaît sûrement l\'alimentation des tortues.', '184000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('735', 'Entraînement au marteau.', 'Oto a besoin de dix Marteaux de Boisaille pour l\'entraînement de ses troupes.', '3500', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('258', 'Visiter la bibliothèque', 'Visiter la nouvelle bibliothèque.', '5000', '0', '7805', null, '0');
INSERT INTO `quest_step` VALUES ('913', 'Le wabbit et la tortue.', 'D\'après Oshimo, Turorte se serait fait un ennemi mortel. Lelievwe Pâle Pitane aurait signé un pacte avec les démons pour avoir plus de puissance. Il réside sur l\'île des wabbits squelettes.', '191000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('914', 'Spentox, ça gratouille et ça chatouille.', 'Mulic Bere vous a donné accès à sa forge spéciale pour fabriquer un grattoir à dos Spentox.', '198000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('533', 'Retrouver l\'espion.', 'Vous devez rapidement retrouver l\'espion blessé sur la route menant à Bonta, et récupérer les documents. L\'espion doit être plus proche de Bonta que de Brâkmar.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('534', 'Le choix.', 'Vous avez trouvé l\'espion à temps. Mais allez-vous suivre les ordres et récupérer les documents en le laissant là ?', '205000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('535', 'Un petit échec.', 'Des traces de combat, deux armes laissées au sol, un Sram décédé : tout porte à croire que vous n\'êtes pas arrivé à temps.', '205000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('869', 'L\'éternelle moisson.', 'Vous avez la liste des créatures, reste à les trouver.', '882000', '0', '9686,20', null, '0');
INSERT INTO `quest_step` VALUES ('536', 'Attraper l\'espion.', 'Un espion bontarien s\'est infiltré dans la tour et a volé des documents. Il faut utiliser un Zaap pour l\'intercepter, à l\'Est de la route menant à Bonta, du côté de Brâkmar.', '212000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('537', 'L\'art de cuisiner sans se fatiguer, ou presque.', 'Vous devez trouver les recettes des ingrédients listés sur le papier remis par Oto Mustam.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('538', 'Ratatouille.', 'Il faut réunir les ingrédients nécessaires dans le chaudron primordial : un objet de chaque type et un alchimiste, un boulanger, un paysan, un boucher, un poissonnier.', '219000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('539', 'Le côté obscur du wabbit.', 'Vous devez négocier l\'emplacement de la Corne Edfrit contre un Grattoir à dos Spontex auprès de Turorte, mais qui c\'est ce qui vous attend dans la jungle profonde de Moon ?', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('540', 'La fourfouille.', 'Turorte vous a révélé que la Corne Edfrit était enterrée au nord-est de l\'île, près d\'une statue de phénix.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('573', 'Une mise en scène minutieuse.', 'Pour finaliser la mise en scène, Amayiro a besoin de Miteters présentes sur des Craqueleurs et de la Graisse de Gelée Royale, n\'importe laquelle fera l\'affaire.', '233000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('727', 'Offre d\'emploi.', 'Oto Mustam veut savoir qui a assassiné le patron de la Taverne du Chabrulé.', '125', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('728', 'Des potions énergétisantes.', 'Oto Mustam a besoin de trois potions Pahoa Raide.', '250', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('729', 'Un nouveau dentier.', 'Vous devez arracher des dents de Bouftou pour confectionner un dentier. Vous devez en amener exactement assez pour faire un dentier d\'humain... enfin, d\'humanoïde.', '500', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('77', 'Bien jolie cobaye', ' ', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('732', 'Une clé sur un écureuil.', 'Vous devez retrouver un écureuil dans Brâkmar : il a volé une clé importante. Rapportez-la à Oto.', '2000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('733', 'Enquête importante.', 'Faites le tour des gardes de Brâkmar et réalisez un compte-rendu des gardes qui ne prennent pas de pause.', '2500', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('736', 'Un message à faire passer.', 'Oto veut que vous passiez son message à Vil Smisse, dans le repaire des Roublards qui se situe dans le cimetière d\'Amakna.', '4000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('737', 'La récolte de poison.', 'Oto Mustam a besoin de 20 pods de venin de Scorbute.', '4600', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('742', 'La bouillie de blops.', 'Il faut apporter de la bouillie de blop concassés à Oto. Il peut vous donner la recette.', '7600', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('860', 'L\'éternelle moisson.', 'Vous avez la liste des créatures, reste à les trouver.', '4780000', '0', '9689,19;9690', null, '0');
INSERT INTO `quest_step` VALUES ('743', 'A la recherche de poison magique.', 'Vous devez trouver quelqu\'un dans Brâkmar qui sache préparer des poisons magiques.', '8200', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('744', 'Les capes brâkmariennes.', 'Oto vous a demandé cinq capes brâkmariennes. Il peut vous indiquer comment les fabriquer.', '8800', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('745', 'Trouver le testament.', 'Vous devez trouver le testament d\'Oto dans la tombe de son père, dans le cimetière - elle se reconnait par la gerbe de fleurs qui l\'entoure. Vous devez être transformé en esprit pour y entrer.', '9400', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('747', 'Récolter la taxe.', 'Vous devez récolter la nouvelle taxe d\'Oto dans chaque taverne de Brâkmar. Carte blanche pour tabasser les taverniers qui ne veulent pas payer.', '10700', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('748', 'Voler le Poumon.', 'Oto veut que vous voliez le Poumon de la plaine à Azra Lazarus, la bijoutière. Il semble qu\'elle ait un garde, mais peut-être est-il possible d\'éviter de le combattre...', '11400', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('749', 'Le grand épluchage.', 'Vous devez éplucher des patates pour le dîner. La cuisine se trouve en bas de la milice.', '12100', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('750', 'Humilier Sbab ?', 'Oto vous a demandé d\'amener dix oreilles de Tiwabbits à Sbab pour l\'horrifier. Mais il y a peut-être une autre solution si vous ne souhaitez pas le faire.', '12800', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('751', 'Aider Raaga.', 'Trouvez Raaga dans les mines du nord et demandez-lui son rapport.', '13500', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('754', 'Défier Puja.', 'Vous êtes censé défier Puja Mustam, le fils d\'Oto, et perdre. Mais vous n\'allez quand même pas vous laisser tuer par ce mioche ?', '15600', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('756', 'Acheter l\'équipement.', 'Vous devez acheter l\'équipement qu\'Oto a indiqué sur sa liste et l\'équiper avant de retourner le voir.', '17000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('757', 'Un autre rapport.', 'Vous devez trouver Trojen Tihpor vers les remparts de Bonta et récupérer son rapport.', '19000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('861', 'L\'éternelle moisson.', 'Vous avez la liste des créatures, reste à les trouver.', '12852000', '0', '9690,2', null, '0');
INSERT INTO `quest_step` VALUES ('758', 'Trouver un Bontarien pas net.', 'Vous devez trouver un Bontarien coupable d\'assassinat. Pour cela, il faut se procurer un bâton renifleur à Fryda, puis l\'essayer sur des Bontariens jusqu\'à en trouver un qui convienne.', '21000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('759', 'Leçon d\'humilité.', 'Oto vous a ordonné de lui amener 40 yeux de larves bleues.', '23000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('979', 'Vaincre le dopeul.', 'En cas de victoire contre le dopeul, le maître des combats vous récompensera.', '0', '0', '0', '1|7106;', '0');
INSERT INTO `quest_step` VALUES ('763', 'L\'entretien.', 'Une personnalité importante de Brâkmar a demandé à vous voir. Elle se trouve dans les sous-sols de la tour de Brâkmar.', '17000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('764', 'Du vin pour Oto.', 'Vous devez amener à Oto du vin de Gloutovore. Où pourrait-on trouver ça ?', '17000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('767', 'Sortie de prison.', 'Vous devez sauver un guerrier qui a été capturé par Bonta : il se trouve dans la prison de la milice bontarienne, au sous-sol.', '40000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('864', 'L\'éternelle moisson.', 'Vous avez la liste des créatures, reste à les trouver.', '19836000', '0', '9690,12', null, '0');
INSERT INTO `quest_step` VALUES ('792', 'A la recherche du greffier déprimé.', 'Tabach, Greffier de l\'Ordre du Coeur Vaillant, écume les tavernes depuis la disparition de son chat. Il faut le ramener auprès de Jerhyn Gholein.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('793', 'A la recherche de la greffière.', 'Ybaul, Greffière de l\'Ordre de l\'Esprit Salvateur, écume les tavernes à la recherche de ses frères. Il faut la ramener auprès d\'Elviana Tirips.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('794', 'A la recherche du greffier attristé.', 'Aigurraf, Greffier de l\'Ordre de l\'Œil Attentif, écume les tavernes depuis la disparition de sa souris. Il faut le ramener auprès d\'Ebru Of El.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('797', 'Mauvais oeil.', 'Ledrob Terceséc vous a demandé de lui apporter 1 Œil de Corbac pour lui prouver votre valeur.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('847', 'L\'éternelle moisson.', 'Vous avez la liste des créatures, reste à les trouver.', '10000', '0', '9686,19;9687', null, '0');
INSERT INTO `quest_step` VALUES ('853', 'L\'éternelle moisson.', 'Vous avez la liste des créatures, reste à les trouver.', '655000', '0', '9686,19;9687', null, '0');
INSERT INTO `quest_step` VALUES ('877', 'L\'éternelle moisson.', 'Vous avez la liste des créatures, reste à les trouver.', '10783000', '0', '9688,20', null, '0');
INSERT INTO `quest_step` VALUES ('882', 'Le savoir basique.', 'Allez apprendre la technique de la capture d\'âme. La clef du donjon doit se trouver sur les créatures de la région.', '35000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('905', 'Un coup de main.', 'Latem Sa vient de recevoir du minerai et de nouveaux outils. Vous devez profiter de son absence, entre 17 heures et 21 heures, pour dérober le minerai et échanger ses outils.', '122000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('963', 'Un coup fourré.', 'Afin de saboter l\'équipement de Bonta, Oto Mustam veut que vous remplaciez les outils de ses forgerons par 5 Marteaux Martel.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('989', 'Vaincre le dopeul.', 'En cas de victoire contre le dopeul, le maître des combats vous récompensera.', '0', '0', '0', '1|7106;', '0');
INSERT INTO `quest_step` VALUES ('945', 'Engagez-vous, qu\'il disait.', 'Snori Nairb vous a fait entrer illégalement dans la milice de Brâkmar. Pour officialiser la chose, vous devez rendre visite à Oto MUstam.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('946', 'Engagez-vous, qu\'il disait.', 'Snori Nairb vous a fait entrer illégalement dans la milice de Bonta. Pour officialiser la chose, vous devez rendre visite à Amayiro.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('982', 'Vaincre le dopeul.', 'En cas de victoire contre le dopeul, le maître des combats vous récompensera.', '0', '0', '0', '1|7106;', '0');
INSERT INTO `quest_step` VALUES ('985', 'Vaincre le dopeul.', 'En cas de victoire contre le dopeul, le maître des combats vous récompensera.', '0', '0', '0', '1|7106;', '0');
INSERT INTO `quest_step` VALUES ('987', 'Vaincre le dopeul.', 'En cas de victoire contre le dopeul, le maître des combats vous récompensera.', '0', '0', '0', '1|7106;', '0');
INSERT INTO `quest_step` VALUES ('988', 'Vaincre le dopeul.', 'En cas de victoire contre le dopeul, le maître des combats vous récompensera.', '0', '0', '0', '1|7106;', '0');
INSERT INTO `quest_step` VALUES ('990', 'Vaincre le dopeul.', 'En cas de victoire contre le dopeul, le maître des combats vous récompensera.', '0', '0', '0', '1|7106;', '0');
INSERT INTO `quest_step` VALUES ('992', 'Vaincre le dopeul.', 'En cas de victoire contre le dopeul, le maître des combats vous récompensera.', '0', '0', '0', '1|7106;', '0');
INSERT INTO `quest_step` VALUES ('1035', 'Percer le secret de la statue.', 'Persimol a enfoui son secret au pied de la statue. Penchez-vous pour le ramasser.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('1056', 'La vie facile.', 'A peine arrivé, on vous propose déjà un moyen de gagner votre vie facilement et sans trop d\'effort. Que demander de plus ?', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('1058', 'On en viendra tous à bout.', 'Grâce à votre aide, Sel Dawa est en mesure d\'éradiquer le mal qui frappe le marché des boissons pandawas, à condition de trouver un antidote.', '3000', '0', '10818', null, '0');
INSERT INTO `quest_step` VALUES ('711', 'Fausses preuves.', 'Trouvez un moyen de berner Amayiro en lui montrant des fausses preuves. Danathor pourra sans doute vous y aider.', '14900', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('740', 'Les casques de Chafer.', 'Oto veut dix casques de Chafer qu\'on trouve au Cimetière des Torturés.', '6400', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('1048', 'Se déplacer en combat.', 'Votre personnage possède des {ui,banner,lbl_pm,2::points de mouvement} (PM) pour se déplacer. Se déplacer d\'une case coûte 1 PM. Déplacez votre personnage en cliquant sur une case avec le bouton gauche de votre souris. Vous récupérez vos {ui,banner,lbl_pm,2::PM} au début de votre prochain tour.', '5', '0', '10797', null, '0');
INSERT INTO `quest_step` VALUES ('1091', 'Usine à jouets.', 'D\'abominables créatures ont cambriolé l\'entrepôt du Père Nowel. Vous devez récupérer les caisses de cadeaux qu\'elles ont volées, avec ou sans leur consentement, et les apporter à Félix Nowel.', '0', '1000', '10920', null, '0');
INSERT INTO `quest_step` VALUES ('366', 'Se mettre en bière', 'Aymé veut de la bière, pourquoi ne va-t-il pas la chercher tout seul ?', '15', '10', '0', '1|3814;4|-2;', '0');
INSERT INTO `quest_step` VALUES ('367', 'Licence quatre', 'Vous avez la bière, en la donnant à Aymé, vous devriez recouvrer toute votre énergie.', '0', '0', '0', '1|3817;', '0');
