INSERT INTO `quest_step` VALUES ('889', 'Une lettre classée X.', 'Il vous faut trouver l\'identité de ce fameux X. Là lettre fait référence à un lieu où il a l\'habitude d\'aller. Trouver ce lieu permettra d\'en savoir plus.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('879', 'L\'éternelle moisson.', 'Vous avez la liste des créatures, reste à les trouver.', '39370000', '0', '9690,6', null, '0');
INSERT INTO `quest_step` VALUES ('169', 'Zero Dofus', 'Le messager du sage Lenald vous envoie à la lisière du village d\'Aerdala. Sur le chemin, vous méditez ses derniers mots : \" + \"\"\" + \"Plus d\'un aventurier s\'est fait mal accueillir en allant à Aerdala. On les dit alliés des Pandawas rebelles, ceux qui sabotent tous les accès de l\'île...\" + \"\"\" + \" ', '2000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('192', 'Bons baisers d\'Astrub', 'Le shaman est débordé, il n\'a pas de temps à vous consacrer. Entre les Pandikazes, la maladie des Enutrofs d\'Astrub et le reste...Il vous conseille de parler à Pyrite, Enutrof en quarantaine, si vous désirez l\'aider.', '1500', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('222', 'Visite du château d\'Allister', 'Degui Quetiristou te propose de visiter les nouveaux aménagements du château et de son port.', '7000', '50', '0', null, '0');
INSERT INTO `quest_step` VALUES ('1062', 'Vaincre le Manitou Zoth.', 'Vous devez vaincre le Manitou Zoth et apporter une preuve de votre victoire à Bob l\'Enutrof.', '0', '0', '10899', null, '0');
INSERT INTO `quest_step` VALUES ('53', 'Doublure puante', 'Alit vous conseille de rajouter une doublure à votre cape. Mais pour cela il vous faut lui ramener de la laine de bouftou.', '3500', '0', '6801', null, '0');
INSERT INTO `quest_step` VALUES ('240', 'Les ingrédients de la potion de lèche-bottes', 'Corne du Boufcoul, Pétale de Rose Démoniaque, Kralamoure vidé et Graine de Pavot sont nécessaires pour réaliser une bonne potion de lèche-bottes. \n', '6000', '6000', '0', null, '0');
INSERT INTO `quest_step` VALUES ('159', 'Le sage Lenald', 'Une vendeuse de rafraîchissement Pandawaïenne vous conseille de rencontrer le sage Lénald. Selon elle, lui seul détient la vérité sur Pandala et ses secrets.', '1000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('121', 'Trouver et escorter le hors-la-loi', 'Il est probable que Padgref Demoël se trouve dans le Nord Ouest des plaines de Cania, du côté du bois de Litneg. \nIl est condamné à l\'emprisonnement à Astrub pour détérioration de Bouftou domestique et tapage nocturne. \n', '0', '84500', '0', null, '0');
INSERT INTO `quest_step` VALUES ('206', 'Trouver la hors-la-loi', 'Aermyne a été aperçue du côté de Terrdala. \nElle est condamnée à l\'emprisonnement à Astrub. \n', '0', '16000', '0', null, '0');
INSERT INTO `quest_step` VALUES ('156', 'Le Grandapan : Un pandawa sympa', 'Les Enutrofs en cure chez le Shaman vous conseillent d\'aller voir le Grandapan. Très au fait des coutumes locales, il est aussi un fin connaisseur des légendes Pandawaïennes.', '500', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('424', 'Vous ne passerez pas !', 'Le Chevalier Noir et Rose est fermement décidé à ne pas vous laisser passer. C\'est peut-être le bon moment d\'employer la force.', '45000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('44', 'Fleur de Dzibi', 'Pour que votre maîtresse de classe puisse vous confectionner un bâton digne de ce nom, vous devez trouver de la fleur de Dizbi.', '600', '0', '6798', null, '0');
INSERT INTO `quest_step` VALUES ('902', 'Le puits du fou.', 'Vous devez trouver l\'emplacement du Puits des Âmes Souillées afin de révéler le plein potentiel des pierres de souillure. Le maître de l\'Ordre de l\'Esprit Malsain, Divad Dleifrepok, pourra vous aider.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('179', 'Shaman connexion', 'Le shaman est un Pandawa qui en sait beaucoup sur les agissements de certains... Apparemment, tout n\'est pas rose à Pandala !', '3000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('186', 'Shodanwa', 'Il faut se présenter au maître un samedi pour passer l\'évaluation du grade Shodanwa.', '30000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('892', 'Un Sram plus bête que nature.', 'Exoram s\'est encore mis dans de sales draps. Si vous voulez les informations sur Xephirès, il va falloir l\'aider une nouvelle fois.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('918', 'Exoram le Sram.', 'Vous avez ramené Exoram au temple Sram. Cette fois-ci il a intérêt à vous donner les informations qu\'il s vous a promis.', '70000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('331', 'Une baguette et une panoplie', 'Chef Bworkestre a besoin de prouver sa virilité à sa douce et tendre en demandant des kdô aux visiteurs de passage dans son charmant village. \nAvec une panoplie Prespic, il espère repousser les coups de rouleau à pâtisserie de sa bworkette d\'amour.', '45000', '35000', '0', null, '0');
INSERT INTO `quest_step` VALUES ('741', 'Chasse au loup.', 'Oto vous a demandé de débarrasser Brâkmar d\'un loup qui rôde autour de la façade.', '7000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('347', 'Je fais du social', 'Disskray veut savoir si les villageois n\'ont rien remarqué d\'étrange ou d\'inhabituel', '150', '0', '8535', null, '0');
INSERT INTO `quest_step` VALUES ('216', 'Obtenir le faux livre d\'Acidrik', 'Ce livre se nomme \" + \"\"\" + \"le temps des dragons\" + \"\"\" + \" et non \" + \"\"\" + \"le Temps des Dragons\" + \"\"\" + \".', '1000', '200', '0', null, '0');
INSERT INTO `quest_step` VALUES ('188', 'Dans le temple de votre dieu', 'Suivant les conseils du sage Lenald, vous préparez votre prochaine expédition. Pour cela il vous faut vous rendre dans le temple de votre divinité en Amakna.', '7500', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('403', 'Un oeuf tout neuf', 'Vil Smisse vous a demandé de lui rapporter un oeuf de tofu obèse. Encore faut-il trouver le nid...', '5000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('320', 'Jeu Trooll règles avancées', 'Raxitik est terrorisé. Il sait que les Bworks vont le livrer au Trooll pour qu\'il pratique une partie de jeu Trooll règles avancées avec lui. Il faut absolument éliminer ce Trooll qui menace l\'intégrité physique de Raxitik.', '30000', '0', '2562', null, '0');
INSERT INTO `quest_step` VALUES ('926', 'Liquidation totale.', 'Une Sram doit de l\'argent à Oto Mustam. Comme elle ne l\'a pas remboursé, il a décidé de l\'éliminer à titre d\'exemple. Vous pourrez probablement la trouver dans une salle de jeux clandestine.', '233000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('193', 'Le réceptacle aux Dofus', 'Le Grandapan a accepté le cadeau des Enutrofs avec joie. Fervent admirateur des Dofus, il vous accueille à bras ouverts. Pour satisfaire votre curiosité à propos des Dofus, il vous invite à aller voir le réceptacle aux Dofus.', '1500', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('385', 'Trouver le repaire des Roublards.', 'Yse Vewybad vous a donné des indications sur la position des Roublards. Allez donc voir du côté du cimetière.', '9000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('252', 'Pauvre pomme', 'Gemfé veut que l\'on remercie Rocco Fidelio pour ses patates.', '2500', '1200', '0', null, '0');
INSERT INTO `quest_step` VALUES ('9', 'Chacha héros ?', 'Trouver ce qui fera de ce frêle Chacha un héros auprès de sa compagne.', '3060', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('165', 'Qui a peur du méchant chacha', 'Poochan recherche son futur mari, les seuls indices qu\'elle a sont qu\'il doit toujours se trouver à Pandala, et qu\'il a peur des chachas. Le plus simple est encore de se transformer en chacha et de sauter dans les bras de tout le monde non ?', '1000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('482', 'En territoire ennemi.', 'Vous devez vous infiltrer dans le bâtiment de la milice de Bonta et découvrir où vos ennemis entreposent leurs armes.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('899', 'Les andouillettes d\'une andouille.', 'Oto Mustam étant persuadé que vous cherchez à le tromper, il faut impérativement retrouver le joyau avant d\'être transformé en andouillettes.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('102', 'Bois d\'Arash', 'Procurez-vous du bois d\'Arash : en sacrifiant la nature, vous pourrez vous lier avec elle.', '1200', '0', '6853', null, '0');
INSERT INTO `quest_step` VALUES ('919', 'Un Eca ça flippe énormément.', 'Vous pouvez à présent fabriquer des clefs et inspecter le repaire des bandits à la recherche d\'indices. Une corde d\'escalade ne sera pas de trop pour descendre dans le repaire.', '80000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('371', 'Il est à qui le Kiki ?', 'Ma\'Ster vous a mis au défi de battre le monstre tapi dans sa niche.', '0', '0', '0', '1|3830;', '0');
INSERT INTO `quest_step` VALUES ('470', 'Au rapport.', 'Le convoi a été attaqué par des bandits Ecaflips semblables à ceux dont vous avez découvert le campement. Amayiro attend votre rapport pour convaincre le conseil d\'agir.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('162', 'L\'accent sur le voyage', 'Le sage Lenald vous demande d\'aller au village de l\'Aerdala...ou de Terrdala, impossible d\'être sûr à cause de son accent. Une fois sur place, parlez au maître Pandawushu.', '750', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('339', 'Plumer Gobrose', 'Gobmichet est jaloux des pious de Gobrose. Seul les vibrations de son petit coeur de gobelin vert doivent faire palpiter celui de Gobrose.', '3000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('319', 'Trouver la recette de la bière Bwork', 'Pinta Gone recherche la recette de la fameuse bière Bwork. Il pense qu\'elle se trouve dans une salle étrange dans le territoire Bwork.', '2500', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('296', 'La dernière clef.', 'Kal vous a remis la dernière de ses clefs. Vous approchez du but.', '450000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('423', 'Le Coffret Maudit du Flib', 'Le bateau du Capitaine Kradoc s\'est lamentablement échoué sur l\'île des naufragés. Vous devez retrouver le Coffret Maudit du Flib et quelques objets pour réparer le bateau.', '30000', '2500', '0', '1|4133;', '0');
INSERT INTO `quest_step` VALUES ('891', 'Une mission loin d\'être achevée.', 'Des traces de lutte et des restes de karne indiquent qu\'Exoram s\'est battu et a été capturé ou tué. Oto Mustam sera furieux de l\'apprendre.', '65000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('401', 'Après la recette, les ingrédients.', 'Maintenant qu\'Uba a la formule, il ne lui manque plus que les ingrédients.', '25000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('720', 'Le tabi de Grace.', 'Grace Malcean, dans Bonta, a des problèmes avec son tabi.', '25000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('142', 'Encore de la collecte...', 'Les Crâs tirent leur épingle du jeu, ou plus précisement, leurs flèches. Les Araknes malades semble contenir dans leurs pattes de quoi faire du poison... business is business...', '750', '75', '0', null, '0');
INSERT INTO `quest_step` VALUES ('160', 'Un tiens vaut mieux que deux tu boiras', 'La voie de l\'eau consiste à résister à... l\'absorption et à l\'immersion (partielle et totale) d\'eau par tous les moyens. Ne jamais en boire, se laver uniquement en cas de nécessité. Trouvez le maître de la voie de l\'Eau : il en sait plus qu\'il n\'y paraît', '9000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('45', 'Besoin de main-d\'oeuvre', 'Trouvez Howard le Simaisrien et remettez-lui la lettre de Joe Pierlivet.', '600', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('76', 'La nécessité de savoir s\'en servir', 'Apprenez le maniement de votre nouvelle arme en attaquant quelques insectes.', '900', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('248', 'Monture et huile pour les vacances', 'Monabella a besoin d\'une dragodinde et d\'huile de tournesol pour partir se reposer au soleil dans sa future paillote.', '140000', '5000', '0', null, '0');
INSERT INTO `quest_step` VALUES ('922', 'Complicité accrue.', 'Afin de pénétrer dans les réserves de poison de Brâkmar, vous aurez besoin des services d\'un Sram ou d\'un espion qualifié.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('900', 'Vengeance, mouahaha.', 'Le repaire des bandits est piégé. Il vous faut trouver un Bricoleur de niveau 30 minimum pour vous accompagner et vous aider à désamorcer les pièges protégeant leur cachette.', '100000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('132', 'Bats toi si t\'es une Feca', 'Le célèbre Fallanster vous a demandé d\'entraîner sa fille au combat.', '1200', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('404', 'Gavage', 'Vous avez l\'oeuf de Tofu obèse, mais un oeuf ne va pas faire grand mal au pwince... Il faut trouver de quoi gaver l\'oisillon à naître.', '40000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('21', 'Ca va saigner !', 'Il vous prend le besoin d\'en savoir plus sur vous-même et vous décidez d\'aller dans un endroit sanglant... la boucherie.', '600', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('890', 'Une mission presque achevée.', 'Le chemin est à présent dégagé, même si les gobelins ne constituaient pas une réelle menace. Exoram va enfin pouvoir vous révéler ce qu\'il sait et Oto Mustam sera fier de vous.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('309', 'Le tour du village', 'Les villageois ont sûrement vu les enfants El. Interrogez certains d\'entre eux pour obtenir des informations.', '35000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('289', 'Un message très clair.', 'Kal, une sorte de machine, vous aide à poursuivre votre quête. Avec lui au moins, les objectifs sont clairs.', '250000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('365', 'La clef entre les dents', 'Si tu veux la clef du donjon, il faut battre un gros bouftou hargneux.', '55', '0', '8545', '1|3815;', '0');
INSERT INTO `quest_step` VALUES ('405', 'Un cadeau empoisonné', 'Vil Smisse a décidé d\'éviter l\'approche directe et de faire les choses plus à la façon traditionnelle des Roublards - sournoisement.', '10000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('134', 'Des cheveux... toujours des cheveux...', 'Phite cherche à récuperer la totalité des types de cheveux des habitants d\'Astrub, pour en faire des postiches.', '3000', '150', '0', null, '0');
INSERT INTO `quest_step` VALUES ('217', 'Rapporter une plume de Piou violet à Acidrik', 'Il en a besoin pour lever la magie d\'Herdegrize sur le plagiat de son livre.', '1500', '100', '7930', null, '0');
INSERT INTO `quest_step` VALUES ('311', 'Un plat unique', 'La cruelle Paindep menace de dévorer les enfants El si elle n\'a rien d\'autre de mieux à déguster.', '75000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('232', 'Kartier Tremai préfère trinquer avec un ami', 'Il lui faut de la bière et un ami pour passer une bonne soirée.', '7500', '3000', '0', null, '0');
INSERT INTO `quest_step` VALUES ('953', 'Conduite en prison.', 'Vous devez emmener Sabine de force devant Oto Mustam. Vous devez faire attention à ce qu\'elle ne s\'échappe pas à la première occasion.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('956', 'Ordre ou contrordre ?', 'Le plan d\'attaque actuel n\'étant pas confirmé, vous devez trouver sa confirmation ou ses modifications auprès de l\'officier Kapi Taigne et rapporter ces informations à Amayiro.', '164000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('345', 'Relève de la garde', 'Disskray vous a demandé d\'aller contrôler que les gardes font bien leur travail.', '230', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('468', 'Piège pour Ecaflips.', 'Amayiro veut tendre une embuscade aux bandits, pour cela il a besoin d\'un bijou de grande valeur afin de les attirer dans son piège.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('352', 'Transformation', 'Poss\'Ybel veut que vous lui fournissiez quelques produits finis issus du fruit de vos récoltes.', '50', '15', '0', null, '0');
INSERT INTO `quest_step` VALUES ('75', 'Arc-Crâ-Daba-Gras', 'Ha comme c\'est beau de faire fabriquer son premier arc.', '1200', '0', '6783', null, '0');
INSERT INTO `quest_step` VALUES ('901', 'Alchimie malfaisante.', 'Vous devez vous procurer des pierres de souillures. Seuls les mineurs sont capables d\'en fabriquer à partir des créations brutes d\'un alchimiste déjanté.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('209', 'Le secret de Terra Tibya.', 'Terra Tibya est enfin ressuscitée. Reste à la persuader de vous révéler son secret.', '200000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('917', 'Clef, sans molette.', 'Vous devez faire des copies de la clef du repaire des bandits avant de pouvoir y pénétrer à plusieurs et d\'y récupérer tout document relatif à l\'enquête.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('435', 'Chasse dans la Jungle Sombre', 'Crocodaille Dandi aimerait que vous chassiez pour lui quelques créatures de la Jungle Sombre.', '250000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('526', 'Le Mage Ax.', 'Amayiro vous a demandé d\'interroger le Mage Ax pour connaître le mode d\'emploi de la Corne Edfrit et ses pouvoirs.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('185', 'Passage de grade Rokwa', 'Il faut se présenter au maître un samedi pour passer l\'évaluation du grade Rokwa.', '30000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('20', 'Discrétion assurée', 'Trouvez Ogivol Scalarcin, vous devriez trouver l\'entrée de son repère grâce à une trappe dérobée dans le sol du dépôt de bois.', '600', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('924', 'A la tour.', 'Xarthan a rendez-vous avec un Brâkmarien dans la Tour de Bonta après la réunion des notables. Il vaut mieux vous dépêcher pour ne pas le rater.', '95000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('328', 'Chaperonner Titi', 'Le père de Titi aimerait qu\'il devienne mage. Pour cela, il faut l\'amener chez le plus puissant des Bworks Mages avec de quoi manger.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('716', 'Trouver une faille.', 'Amayiro vous a demandé d\'explorer le Cimetière des Torturés, à l\'Est de Brâkmar, et de voir s\'il y a une faille dans les défenses de la cité. Vous aurez sûrement besoin de matériel particulier.', '19000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('72', 'Voleur de coeur', 'Les petits cailloux proviennent des alentours des mines au nord-est du village. Trouvez-y le coffre de Louc.', '4000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('283', 'Ne pas traîner', 'Chris ne va pas mieux, il est préférable de se dépêcher avant que son état empire.', '135000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('82', 'Un remède de Tabi', '  ', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('480', 'Retour vers Oto.', 'La bataille est terminée, Oto Mustam attend votre rapport.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('322', 'Krazybwork veut redevenir débile.', 'Krazybwork a trop mangé de Goblins lors de la bataille de Bworkdrift. Il est devenu intelligent et veut redevenir débile. Le mieux serait de lui mettre un bon coup sur la tête !', '0', '500', '8527', null, '0');
INSERT INTO `quest_step` VALUES ('170', 'Un nouveau message du sage', 'Un messager vous apporte un message, parlez-lui !', '5000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('199', 'Lève-toi et marche.', 'Divad ne parvient pas à ressusciter Terra Tibya pour l\'interroger. Il faut donc improviser un sort de résurrection qui en soit capable et c\'est la mission qui vous a été confiée.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('26', 'Recherche Jambes rapides', 'Le Boulanger a besoin de livrer 4 personnes dans l\'urgence. Aller déposer les gâteaux chez chacun le plus vite possible.', '1000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('233', 'Un coup d\'pompe aux forçats', 'Vous avez maté les prisonniers d\'Astrub. Parlez-en à Edgg Komb.', '1000', '50', '0', null, '0');
INSERT INTO `quest_step` VALUES ('263', 'Une récolte facile', 'Des orchidées poussent dans les montagnes Koalaks. Il faut en rapporter une bonne brassée à Maskara.', '7500', '4000', '0', null, '0');
INSERT INTO `quest_step` VALUES ('927', 'Copinage.', 'Kosuke Ichazum avait raison : Latem Sa a obtenu le contrat pour équiper la milice de Bonta par copinage et non pour ses compétences de forgeron.', '116000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('909', 'Dompteur de bêtes, mais aussi d\'animaux.', 'Oto Mustam veut connaître le secret du domptage des crocs glands. Vous devez retourner à la bibliothèque de Bonta pour y trouver des indices.', '134000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('203', 'Trouver le hors-la-loi', 'Marzwel a été aperçu du côté d\'Aerdala \nIl est condamné à l\'emprisonnement à Astrub. \n', '0', '25000', '0', null, '0');
INSERT INTO `quest_step` VALUES ('431', 'Deux Dragodindes pour Flip Flap', 'Flip Flap voudrait que vous lui rameniez un certificat de Dragodinde Rousse ainsi qu\'un certificat de Dragodinde Amande.', '30000', '30000', '0', null, '0');
INSERT INTO `quest_step` VALUES ('437', 'Un exemple pour les Pirates', 'Kito vous a demandé de faire un exemple pour les Pirates qui lui lancent des boulets.', '30000', '1000', '0', null, '0');
INSERT INTO `quest_step` VALUES ('531', 'L\'ami des bêtes.', 'Vous devez trouver d\'où vient Turorte. Un spécialiste des animaux pourra vous aiguiller dans vos recherches. Oshimo, responsable du chanil, est un bon point de départ.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('865', 'L\'éternelle moisson.', 'Vous avez la liste des créatures, reste à les trouver.', '42923000', '0', '9686,20', null, '0');
INSERT INTO `quest_step` VALUES ('157', 'La statue de la déesse', 'Le Grandapan a accepté le cadeau des Enutrofs avec joie. Fervent admirateur des chercheurs de Dofus, il vous accueille à bras ouverts.', '750', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('351', 'Le fil de l\'histoire.', 'Pour apprendre l\'histoire de l\'épée de Iop vous pouvez aller voir Tenva au temple.', '75', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('25', 'Ca sang le roussis !', 'Votre maître a entendu qu\'un mineur a récemment été changé en mineur sombre. Il veut en avoir l\'artère net !', '1200', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('932', 'La poupée enchantée.', 'Vous devez trouver quatre Sadidas, suffisamment expérimentés pour invoquer des Gonflables, pour un rituel dans les eaux enchantées des lacs Koalaks.', '146000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('279', 'Passer à la suite', 'Pat Akess vous a dirigé vers la personne suivante qui peut vous aider à prouver votre valeur.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('514', 'C\'est l\'or, Moon.', 'Le soi-disant gardien de la Corne Edfrit serait dans la jungle profonde de l\'île de Moon. D\'après les rumeurs, vous devez porter une Fourbacoiffe et avoir du Gloutoburg pour qu\'il apparaisse.', '177000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('375', 'Comme vous avez de grandes dents.', 'Un Mulou vous fait une proposition alléchante.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('519', 'L\'espion blessé.', 'Vous devez rattraper l\'espion blessé qui fait route vers Brâkmar et lui reprendre les documents. \nCependant, vous avez trouvé une lettre dans votre sac...', '205000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('158', 'Libations pour la déesse', 'Grâce à vous, l\'Assistant du Shaman peut commencer son rituel de remerciement à la déesse. Il sera fin prêt dès que vous lui aurez remis une offrande pandawaïenne traditionnelle.', '750', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('427', 'Une histoire de farines', 'Paulette Pain-bèche la boulangère a l\'intention de recouper sa farine de Xavier le Boulanger avec différentes farines bon marché. Elle a eu l\'idée, à vous de trouver les farines !', '30000', '1500', '0', null, '0');
INSERT INTO `quest_step` VALUES ('430', 'L\'équipement amélioré', 'Spitte Fayeur recherche un casque et une cape de pilote améliorés. Seul un tailleur expérimenté pourra vous aider.', '30000', '4000', '0', null, '0');
INSERT INTO `quest_step` VALUES ('440', 'L\'échange maudit', 'Farle Ingalsse est sûrement la seule personne d\'Amakna capable de rendre utilisable cette épée maudite...', '0', '0', '8992', null, '0');
INSERT INTO `quest_step` VALUES ('326', 'Ne pas oublier l\'essentiel : la moule !', 'Bworknroll a besoin d\'une moule qui sort de l\'eau pour finir sa créature de rêve.', '230000', '5000', '0', null, '0');
INSERT INTO `quest_step` VALUES ('61', 'Un métal douteux', 'Apporter le sac de kamas douteux à une personne qui pourra en tirer quelque chose d\'intéressant.', '600', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('153', 'Y\'a du soleil à Pandala !', 'A votre arrivée dans Pandala, vous êtes invité à vous mettre en règle avec l\'administration : régler la taxe sur la Pandrista s\'impose.', '1000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('724', 'Dans les geôles de Brâkmar.', 'Amayiro vous a demandé de sauver Houé Dapyus, dans la prison de Brâkmar, et de le ramener à la milice bontarienne.', '37000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('376', 'Inscription.', 'Si Bworknroll accepte le paiement, Titi pourra devenir mage.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('422', 'Bien préparer son voyage', 'Le Capitaine Kradoc a besoin que vous lui rapportiez plusieurs objets pour qu\'il puisse vous emmener dans son bateau.', '30000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('753', 'Retrouver les documents.', 'Les documents secrets n\'étaient pas sur l\'espion. La faune des landes de Sidimote s\'en est peut-être emparée.', '14900', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('895', 'Le crime va payer, foi de Brâkmarien.', 'Les Bontariens ont essayé de délivrer Sabine. Ca ne se passera pas comme ça ! Vous devez leur montrer qui commande.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('428', 'A la recherche du fils caché', 'Le Docteur Nauh a perdu son fils dans un laboratoire infesté de terribles créatures sauvages. Vous devez le retrouver et vous assurer qu\'il est encore en vie !', '30000', '1000', '0', null, '0');
INSERT INTO `quest_step` VALUES ('346', 'Arietta Fiolle', 'Allez voir où se trouve Arietta Fiolle.', '25', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('429', 'Le docteur est morose', 'Le pauvre Docteur Morose a perdu sa femme. Aidez-le à la trouver dans la Jungle Sombre et tentez de la persuader à retrouver son mari pour qu\'il puisse reprendre ses recherches.', '30000', '1000', '0', null, '0');
INSERT INTO `quest_step` VALUES ('150', 'La récompense', 'Allez chercher votre récompense chez Louc Drif.', '5000', '0', '1519', null, '0');
INSERT INTO `quest_step` VALUES ('47', 'Livraison express', 'Vous devez rapporter l\'infusion à votre maîtresse de guilde pour qu\'elle puisse fabriquer votre bâton ! \nSi au passage vous avez le temps et les moyens d\'aller apporter de l\'infusion à votre fournisseur de fleur, cela lui fera grandement plaisir.', '1200', '0', '6781', null, '0');
INSERT INTO `quest_step` VALUES ('487', 'Pénétrer dans la bibliothèque.', 'Les espions à Bonta ont échoué dans leur assassinat, il faut donc le faire à leur place. La cible est un tacticien qui réside dans la bibliothèque de Bonta mais gare aux chiens de garde.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('333', 'Montrer ses talents', 'Vos talents se développent mais tout le monde n\'en est pas convaincu.', '370', '0', '8536', null, '0');
INSERT INTO `quest_step` VALUES ('191', 'Yondanwa', 'Il faut se présenter au maître un samedi pour passer l\'évaluation du grade Yondanwa.', '60000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('715', 'Activités illicites.', 'Des évènements louches se trament dans le quartier des boulangers. Amayiro vous a demandé d\'enquêter.', '17000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('127', 'Boudin Bouilli de Bouftou', 'Sauver la tradition du bon Boudin Bouilli de Bouftou', '1200', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('301', 'Le maître décolle.', 'Ca y est enfin, le maître des clefs vous a confié votre dernière épreuve.', '875000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('462', 'Enquête à l\'Est.', 'D\'après le tavernier Bagrutte, le marchand avait l\'habitude de rentrer chez lui, un peu plus à l\'Est.', '60000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('680', 'Interroger la Dame.', 'Vous devez aller voir Dame Cloude pour lui demander ce qu\'il se passe.', '125', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('925', 'Pas d\'issue pour les traîtres.', 'Vous devez faire le tour des miliciens pour les poster à la Tour de Bonta.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('28', 'Un livreur dragueur', 'Vérifier si le livreur : Louc Drif, est réellement malade.', '2000', '500', '0', null, '0');
INSERT INTO `quest_step` VALUES ('310', 'La petite cabane dans la forêt', 'Le jeune Jorg a parlé d\'une cabane secrète dans la forêt des dragodindes.', '25000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('704', 'A la rescousse de Lyeno.', 'Vous devez sauver le colonel Lyeno : il a disparu dans la partie Est de la forêt des Abraknydes.', '12800', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('697', 'Enquête sur la foire.', 'Il faut chercher un formulaire auprès de Ture Inge, dans la milice, puis interroger le chef de la foire du Trool afin de détecter s\'il est un Brâkmarien ou non.', '8200', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('291', 'Défaillance technique.', 'Kal n\'a pas résolu ses problèmes mais cela ne vous empêche pas de poursuivre votre quête.', '350000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('884', 'Réengagez-vous, qu\'il disait.', 'Snori Nairb a promis de partager ses kamas et son expérience avec vous si vous vous engagez dans la milice de Bonta.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('65', 'Je veux plus rien !', 'Je veux moins d\'xp et moins de kamas', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('721', 'Raid chez Brâkmar.', 'Amayiro vous a demandé de vaincre des guerriers brâkmariens et de récupérer dix badges sur eux.', '28000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('698', 'De la bouillie pour le Tabi.', 'Apporter de la bouillie de fleurs de blops à Amayiro. Pour obtenir cette bouillie, il faut concasser quatre fleurs de blops de couleurs différentes dans un Concasseur Munster.', '8800', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('465', 'Jolie minette.', 'Votre rencontre avec les bandits vous a appris que Hersa Griffe Sombre en sait plus qu\'elle ne veut l\'admettre. Reste à lui parler sans se faire défigurer.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('91', 'Le futur c\'est l\'aventure !', 'Montrakwartz a besoin d\'une peau de larve bleu pour terminer sa machine.', '1200', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('195', 'Ecuyer, Palefrenier, même combat.', 'Des Brâkmariens ont détruit des greniers bontariens. Il faut  trouver un moyen de ravitailler les montures de la milice en allant trouver une solution auprès de Merlan, à l\'atelier des alchimistes.', '50000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('282', 'Allez voir ailleurs', 'Chris est très légèrement stressé. Mieux vaut faire rapidement ce qu\'il demande.', '125000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('40', 'Livraison de la mine', '', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('897', 'Intimidation intime.', 'Il faut intimider Pandalida en saccageant sa maison. Vous avez besoin de 3 Pâtes à Tra Khben, pâte obtenue en concassant 15 Groins du Sanglier, 5 Pots de Gloutovore et 1 Plume de Serpiplume.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('394', 'Trouver le prétendant.', 'Il vous faut trouver un prétendant pour Allisteria, la fille du frère du roi.', '15000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('512', 'Sufokia.', 'Une rumeur affirme qu\'il existe une corne disposant de puissants pouvoirs magiques. Les habitants de Sufokia auraient des informations supplémentaires.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('178', 'Boire et déboires', 'Un marchand ambulant vous vendra certainement une de ces boissons fermentées qui ont fait la réputation de l\'île. Il vous suffit d\'en trouver un !', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('448', 'Sur les traces du Sram.', 'Exoram a été relâché vivant par les gobelins. Vous devez à nouveau partir sur ces traces pour lui mettre la main dessus.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('51', 'Besoin d\'une pelle', 'Retournez faire votre rapport à Joe pour obtenir votre récompense.', '1200', '0', '6786', null, '0');
INSERT INTO `quest_step` VALUES ('154', 'Pandrista à gogo !', 'Trouvez un marchand ambulant : vous pourrez lui régler la taxe sur la Pandrista. N\'hésitez pas à insister lourdement.', '1000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('168', 'Petit entrainement au bouclier', 'Pour vous remercier de la livraison, le forgeur de bouclier vous a fait cadeau d\'un petit bouclier d\'entraînement, Parlez au maitre du Dojo pour un petit entraînement.', '1000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('177', 'A la santé de la déesse !', 'Nouvel arrivé à Pandala, l\'Assistant Shaman vous demande de faire honneur à votre déesse : une offrande spiritueuse serait la bienvenue... Votre initiation commence.', '500', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('175', 'Legende Lenalde', 'Enfin ! Vous touchez au but ! Mais vous n\'êtes pas au bout de vos surprises ! Le Lénald de l\'île des Wabbits va vous révéler un grand secret longtemps gardé...', '7500', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('176', 'Dark Vlad, un gars qui en a !', 'Purifiez votre âme et lavez-vous les oreilles : le sage Lénald vous révèle la vérité sur le Dofus de Pandala.', '40000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('299', 'Le maître étalon', 'Le maître vous prend pour une vraie bête de guerre, encore une épreuve.', '725000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('196', 'Dans la peau d\'un espion.', 'Suite à l\'incendie d\'entrepôts de nourriture par les Brâkmariens, Ebru Of El veut savoir ce que les ennemis manigancent.', '50000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('862', 'L\'éternelle moisson.', 'Vous avez la liste des créatures, reste à les trouver.', '8700000', '0', '9688,20', null, '0');
INSERT INTO `quest_step` VALUES ('893', 'La rançon.', 'Sabine étant sous bonne garde, vous devez trouver un moyen de contacter le Captain Starky pour exiger un Cristal de Kain Dharyn comme rançon.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('765', 'Anéantir les tortues.', 'Oto vous a demandé de tuer dix tortues de l\'île de Moon. Leurs yeux feront une bonne preuve.', '17000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('143', 'Chasse sauvage', 'De sombres créatures ruinent les affaires d\'Ant Emicheun, s\'en débarasser lui semble être une solution... du moins en attendant de comprendre ce qu\'elles font ici...', '5000', '750', '0', null, '0');
INSERT INTO `quest_step` VALUES ('916', 'Minou minou.', 'Vous devez mettre la main sur la clef du repaire, ce qui induit forcément de mettre la main sur un bandit la possédant. L\'ex-amant d\'Hersa devrait faire l\'affaire.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('7', 'De la boubouffe', 'Se renseigner sur la nourriture d\'un Chacha moyen auprès de ceux que vous rencontrez. Et ramenez un peu de nourriture à ce Chacha.', '1220', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('904', 'Un coup de pouce.', 'Oto Mustam est très satisfait du travail du forgeron équipant la milice de Bonta et vous charge de faire en sorte qu\'il continue dans cette voie.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('364', 'Conditions d\'échange', 'Anta vous a fait une liste de ce qu\'elle veut en échange de son collier magique.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('894', 'Le crime ne paie pas.', 'Le message a été envoyé au Captain Starky. En attendant sa réponse, vous devriez aller voir si Sabine s\'est tenue tranquille.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('457', 'Des getas pour Milone.', 'Pour vous punir de ne pas avoir pu empêcher les voleurs de dérober le bijou, Milone vous a demandé de lui amener des Getas Feudala.', '90000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('374', 'Titi a sa bourse d\'étude.', 'Bworknroll a accepté d\'enseigner la magie à Titi. Annoncer la nouvelle à son père.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('166', 'Arrangement à l\'amiable', 'La coutume ici veut que le père décide qui son fils ou sa fille épousera. Vous devez donc convaincre le père de vous céder son fils. Il faudra faire tout ce qu\'il faut pour ça.', '1000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('67', 'Comment forger une épée\' pour les nuls', 'Vous avez besoin d\'un forgeron pour créer une épée.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('332', 'Tonte matinale', 'Jicé est un type génial, il a compris qu\'avec de la laine on pouvait faire... de la laine', '120', '150', '0', null, '0');
INSERT INTO `quest_step` VALUES ('259', 'Au service du roi', 'Le roi aime monter, il faut lui trouver des dragodindes', '75000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('1050', 'Passer son tour.', 'Lorsque vous avez terminé vos actions, cliquez sur le bouton {ui,banner,btn_skipTurn::fin de tour} avec le bouton gauche de votre souris pour accélérer le déroulement du combat et regagner vos {ui,banner,lbl_pa,2::PA} et vos {ui,banner,lbl_pm,2::PM}.', '5', '0', '10799', null, '0');
INSERT INTO `quest_step` VALUES ('1044', 'Equiper un objet.', 'Ouvrez votre interface de {ui,banner,btn_character,1::caractéristiques}. Sélectionnez l\'{ui,storage,grid::Anneau du débutant} que vous avez gagné en maintenant enfoncé le bouton gauche de votre souris et déposez-le  dans l\'{ui,characterSheetUi,slot_2,2::emplacement réservé à l\'anneau} de votre fiche de personnage. \nVous pouvez également l\'équiper en effectuant un double clic avec le bouton gauche de votre souris sur l\'{ui,storage,grid::Anneau du débutant}.', '5', '0', '10793', null, '0');
INSERT INTO `quest_step` VALUES ('1042', 'Déplacement sur la carte.', 'Bienvenue dans le tutorial guidé. Cliquez avec le bouton gauche de votre souris autour de votre personnage pour le déplacer. Si vous cliquez loin de votre personnage il courra, sinon il marchera.', '5', '0', '10792', null, '0');
INSERT INTO `quest_step` VALUES ('242', 'Une potion de lèche-bottes pour une affaire juteuse', 'Haraquiri voudra peut être de la potion pour ses affaires avec Allister.', '16500', '1000', '0', null, '0');
INSERT INTO `quest_step` VALUES ('180', 'Passage de grade Ikwa', 'Il faut se présenter au maître un samedi pour passer l\'évaluation du grade Ikwa.', '5000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('315', 'Service express', 'La Filtounga de Pol doit être prête maintenant.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('712', 'Amener le venin.', 'Le capitaine Essacarf, dans la prison de Bonta, a besoin de 30 pods de venin de scorbute.', '15600', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('286', 'Encore un', 'Vous sentez que votre quête avance... lentement.', '165000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('939', 'Retour au bercail.', 'Il est temps de rentrer à Bonta et de faire le point avec Amayiro.', '226000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('325', 'A découper en suivant les pointillés', 'Bworknroll a besoin de remplir sa créature d\'organes frais (et juteux).', '150000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('80', 'Souterrain ténébreux', 'Une porte dérobée dans la montagne donne accès à un souterrain, encore faut il trouver comment...', '2500', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('342', 'Une recette pour Pipelette', 'Pipelette attend la recette des crêpes d\'Astrub pour préparer la venue de ses petits enfants. ', '0', '75', '0', '1|3659;', '0');
INSERT INTO `quest_step` VALUES ('896', 'Le cristal sombre.', 'Vous devez prendre le passage secret dans l\'atelier des alchimistes avec des maîtres dans leur métier. Il vous faut en plus une Potion de Rafale, une Harmonie et une Kobalite.', '85000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('273', 'Pour le titre', 'Pat vous sert d\'arbitre pour cette partie de la quête.', '45000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('300', 'Le maître aux normes', 'Il semble que vous ne puissiez échapper à cette interminable liste de massacres. Si il faut en passer par là...', '800000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('986', 'Vaincre le dopeul.', 'En cas de victoire contre le dopeul, le maître des combats vous récompensera.', '0', '0', '0', '1|7106;', '0');
INSERT INTO `quest_step` VALUES ('285', 'Un vrai défi', 'Chris vous envoie dans des endroits de plus en plus dangereux.', '155000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('532', 'La forge de luxe.', 'Il faut fabriquer un grattoir à dos que Turorte puisse utiliser. Etant le meilleur forgeron de Brâkmar, Mulic Bere pourra vous aider à réaliser cet outil particulier.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('420', 'Un déguisement pour Emeraude', 'Emeraude cherche désespérément une panoplie Koalak.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('370', 'Combattre pour la garde.', 'Le capitaine vous a confié une tâche simple mais utile.', '195', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('104', 'Rituel', 'Trouvez la personne qui vous aidera à confectionner votre bâton d\'initié.', '1800', '0', '6782', null, '0');
INSERT INTO `quest_step` VALUES ('723', 'Une leçon à donner.', 'Un certain William Lengland perturbe les habitants de Bonta : il faut aller le calmer, par la force.', '34000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('766', 'L\'attaque bontarienne.', 'Vous devez vous rendre au rempart Est et y vaincre les Bontariens.', '37000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('472', 'Pour vivre, il faut des vivres.', 'Sous la pression du conseil, Amayiro vous a retiré l\'enquête et vous êtes désormais de corvée de ravitaillement.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('854', 'L\'éternelle moisson.', 'Vous avez la liste des créatures, reste à les trouver.', '874000', '0', '9687,20', null, '0');
INSERT INTO `quest_step` VALUES ('73', 'Cours Forest !', 'Vous aimez les enfants ? Celui-ci vous allez le détester...', '600', '50', '0', null, '0');
INSERT INTO `quest_step` VALUES ('270', 'Poursuivre l\'aventure', 'Vous devez continuer à montrer votre détermination.', '30000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('204', 'Trouver le hors-la-loi', 'Zatoïshwan a été aperçu du côté de Terrdala. \nIl est condamné à l\'emprisonnement à Astrub.\n', '0', '112500', '0', null, '0');
INSERT INTO `quest_step` VALUES ('855', 'L\'éternelle moisson.', 'Vous avez la liste des créatures, reste à les trouver.', '1080000', '0', '9687,20', null, '0');
INSERT INTO `quest_step` VALUES ('83', 'Stabiliser le patient', ' ', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('280', 'Passer à la suite', 'Le Maître des clefs vous a envoyé voir le premier de ses amis.', '0', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('349', 'Piou', 'Piou piou piou piou piou !', '250', '0', '6897,2;8537', null, '0');
INSERT INTO `quest_step` VALUES ('983', 'Vaincre le dopeul.', 'En cas de victoire contre le dopeul, le maître des combats vous récompensera.', '0', '0', '0', '1|7106;', '0');
INSERT INTO `quest_step` VALUES ('738', 'Le rapport de l\'espion.', 'Oto vous a demandé de trouver l\'espion Finche Aticus, à Bonta, et de lui demander son rapport.', '5200', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('336', 'Trouver la tanière de Bworkcine', 'C\'est là qu\'est retenue Gobrose.', '1000', '0', '0', null, '0');
INSERT INTO `quest_step` VALUES ('295', 'Il manque un boulon', 'Kal reste bloqué mais vous donne les clefs.', '400000', '0', '0', null, '0');
