INSERT INTO `npc_questions` VALUES ('3261', 'Hum... Tu m\'as l\'air d\'une belle chochotte toi... \nBon t\'as de la chance, je dois retourner à Madrestam. \nAllez monte.', '', '', '', '');
INSERT INTO `npc_questions` VALUES ('2829', '...Hum Terra Cogita à donc élaboré tout ceci dans un seul but... Mais oui, j\'y suis ! Tout simplement pour éviter que cela se reproduise !... Maimane avait donc déjà tout prévu... \n \n* Wally prend son carnet et griffonne quelques schémas *', '', '', '', '');
