INSERT INTO `npc_questions` VALUES ('147', 'Ola, méfie-toi ! Dans nos contrées, les passagers clandestins, on les passe par-dessus bord. \nTu ne possèdes pas assez de kamas pour embarquer sur mon bateau, reviens me voir quand tu auras les moyens.', '', '', '', '');
INSERT INTO `npc_questions` VALUES ('784', '74', '', '', '', '');
