INSERT INTO `npc_questions` VALUES ('2637', 'Poochan ? poochan....Ah la petite pandawette ! Mais c\'est une fille ! Ah c\'est vrai qu\'à Pandala tout est possible...Nakunne pourrait le prendre mal... \nDésolé je ne peux pas, je laisse le choix à Nakunne, débrouillez -vous avec elle. \n', '', '', '', '');
INSERT INTO `npc_questions` VALUES ('2615', 'Poochan se sentir mieux ainsi, à ton tour, toi prendre ça, toi boire extrait  de source chacha et chercher personne ayant peur de toi, si toi trouver, toi prévenir poochan', '', '', '', '');
