INSERT INTO `quetes_objectifs` VALUES ('1093', 'L\'histoire cachée.', 'La légende de Goultard ne raconte pas toute sa vie. Suivez son histoire depuis son enfance pour percer ses secrets.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('963', 'Un coup fourré.', 'Afin de saboter l\'équipement de Bonta,Oto Mustam veut que vous remplaciez les outils de ses forgerons par 5 Marteaux Martel.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('904', 'Un coup de pouce.', 'Oto Mustam est très satisfait du travail du forgeron équipant la milice de Bonta et vous charge de faire en sorte qu\'il continue dans cette voie.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('905', 'Un coup de main.', 'Latem Sa vient de recevoir du minerai et de nouveaux outils. Vous devez profiter de son absence,entre 17 heures et 21 heures,pour dérober le minerai et échanger ses outils.', '122000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('204', 'Trouver le hors-la-loi', 'Zatoïshwan a été aperçu du côté de Terrdala.  \nIl est condamné à l\'emprisonnement à Astrub. \n', '0', ' 112500', '0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1174', 'Alerte à Frigost.', 'Albert paisse craint une épidémie de rube et vous demande de vacciner les malades.', '170000', '0', '11196,1', '0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('519', 'L\'espion blessé.', 'Vous devez rattraper l\'espion blessé qui fait route vers Brâkmar et lui reprendre les documents. \nCependant,vous avez trouvé une lettre dans votre sac...', '205000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1192', 'Ginette,l\'imperfection au féminin.', 'Ginette a été enlevée par un kaniglou. Groméo a tenté de la sauver,en vain. Il vous supplie de la délivrer.', '804000', '25000', '11196,2', '0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1191', 'Réconciliation houleuse.', 'Les Capoués semblent n\'avoir rien à se reprocher concernant le vol de céréales,mais quelque chose cloche. Reste à découvrir quoi.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1190', 'La vérité sort aussi de la bouche des adultes.', 'Ginette a un plan pour découvrir ce qui s\'est réellement passé concernant le vol et c\'est vous qui allez le découvrir.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1189', 'Amour impossible.', 'Groméo et Ginette ne peuvent pas se marier à cause d\'un différent opposant leurs familles. Ils vous implorent de trouver une solution.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1184', 'Extrait d\'une inspection.', 'Larry Tutthe est débordé et a besoin d\'un coup de main.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1183', 'Mets tes bottes.', 'Larry Tutthe a besoin d\'un nouvel assistant et il vous a chargé du recrutement.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('488', 'Retour vers Oto', 'Vous vous êtes occupé du tacticien,il faut retourner voir Oto.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('907', 'Dompteur de bêtes,mais aussi d\'animaux.', 'Oto Mustam veut connaître le secret du domptage des crocs glands. Vous devez retourner à la bibliothèque de Bonta pour y trouver des indices.', '134000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1021', 'Abattre le tacticien.', 'Vous avez décidé de recourir à la solution la plus extrême. La plus Brâkmarienne en somme.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1177', 'Escorter le blessé.', 'Vous devez escorter un chasseur blessé jusqu\'à la bourgade de Frigost,pour qu\'il soit pris en charge par le médecin Albert Paisse.', '201000', '0', '11196,1', '0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1178', 'Escorter le pas doué.', 'Vous devez escorter un chasseur blessé jusqu\'à la bourgade de Frigost,pour qu\'il soit pris en charge par le médecin Albert Paisse.', '201000', '0', '11196,1', '0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1179', 'Escorter le mauvais chasseur.', 'Vous devez escorter un chasseur blessé jusqu\'à la bourgade de Frigost,pour qu\'il soit pris en charge par le médecin Albert Paisse.', '201000', '0', '11196,1', '0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1180', 'A vive allure.', 'Pendant que Djauns surveille son trésor,vous devez récupérer un attelage de chienchiens des neiges et le lui apporter. Le temps vous est compté.', '0', ' 25000', '11196,1', '0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1186', 'Un être s\'éveille.', 'Un alchimiste pourra vous aider à mieux cerner l\'identité de la créature trouvée dans les larmes d\'Ouronigride.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1185', 'L\'eau d\'Ogivol Victor est une chance.', 'Ogivol Victor a besoin de votre aide pour prélever de l\'eau minérale afin de l\'analyser et de la rendre potable.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1181', 'YeCh\'Ti Bodoudou.', 'Les Frigostiens ont monté une expédition punitive pour se débarrasser du YeCh\'Ti. Ils ont besoin de Fourrures intactes de Givrefoux pour tromper sa vigilance.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1182', 'Concours d\'affreux bonmonstres.', 'le petit Nico et ses amis font un concours dont vous êtes le jury. Il vous faut choisir lequel des bonmonstres de neige est le plus affreux.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1188', 'Meurtre de sang-froid.', 'L\'esprit Zonier veut se venger de l\'agriculteur qui l\'a assassiné. Il vous a demandé d\'être son bras vengeur.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1187', 'Givrefoux givrés.', 'Des givrefoux complètement givrés s\'en prennent aux voyageurs. Vous devez les vaincre.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('409', 'Qui m\'aime me suive!', 'Le pwince d\'Wa vous a demandé de lui constituer une suite digne de sa stature... Vu ses prouesses au combat,son régime particulier et son sens de l\'esthétisme,il faut bien quatre personnes pour l\'assister.', '45000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('693', 'Apporter la poudre.', 'Vous devez apporter dix pods de poudre à canon à Amayiro. Vous pourrez les acheter à Moub Reicifitra,dans Bonta.', '5800', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('701', 'L\'invocation inconnue.', 'Amayiro vous a demandé de vous renseigner auprès de Zeurg (maître de classe Osamodas) à propos d\'une invocation inconnue employée par Brâkmar.', '10700', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('687', 'Trouver la faille.', 'Vous devez trouver quelque chose d\'intéressant sur les engrenages de la grande porte de Brâkmar.', '3000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('509', 'Vol dans un nid de coucou.', 'Amayiro veut tendre une embuscade aux Brâkmariens pour tester le nouveau matériel et la cohésion des miliciens.', '152000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('494', 'Interception brutale.', 'Vous devez intercepter un convoi d\'armes qui va passer par l\'orée de la Forêt des Abraknydes pour rejoindre le Village des Brigandins. Des renforts seront les bienvenus.', '158000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('477', 'Le vieux bijoutier.', 'Selon le chef des voleurs,le bijoutier de Brâkmar - Milone Corichel - aurait des informations.', '100000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('476', 'Bishom Pot,le retour.', 'Amayiro soupçonne Brâkmar d\'être à l\'origine des problèmes de Bonta. Bishom Pot,espion milicien à Brâkmar,a peut-être appris quelque chose.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('211', 'Souterrain rigoureux', 'Seul huit courageux seront capables de venir à bout de ce souterrain !', '5000', '1000', '0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('81', 'Souterrain mystérieux', 'Une dalle perchée en haut d\'une montagne et une porte derobée... il va falloir trouver des volontaires pour arriver au bout de cette galerie...', '2500', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('74', 'Le sous-sol de la Bibliothèque', 'Comme tous les sous-sols de bibliothèque,on y trouve toute sorte de livres et des toiles d\'araknes... mais dans celle-ci,il y a comme une atmosphère mystérieuse qui y rôde...', '2500', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('80', 'Souterrain ténébreux', 'Une porte dérobée dans la montagne donne accès à un souterrain,encore faut il trouver comment...', '2500', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('163', 'Souterrain impénétrable', 'Gare au Dragoeuf ! Il va falloir traverser leur territoire pour accèder à cette nouvelle grotte...', '2500', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('164', 'Souterrain hasardeux', 'Ce souterrain risque de vous faire perdre patience!', '2500', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('684', 'Visite chez le tavernier.', 'Vous devez récupérer la carte d\'Amayiro et une bière auprès du tavernier Feubuk.', '1500', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('703', 'Patate chaude.', 'Vous devez peler 200 pods de patates et les amener au cuisinier de la milice.', '12100', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('709', 'Un champo pour Amayiro.', 'Amayiro vous a demandé de lui apporter un Champo ordinaire. Vous en trouverez sur les Maîtres Bolets.', '14200', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('327', 'Pinta Gone veut lire la recette', 'Il a déjà acheté tout le matériel pour sa brasserie de bière. Il ne lui manque que la recette.', '7500', '5000', '8145,10', '0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('319', 'Trouver la recette de la bière Bwork', 'Pinta Gone recherche la recette de la fameuse bière Bwork. Il pense qu\'elle se trouve dans une salle étrange dans le territoire Bwork.', '2500', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('523', 'Assaut sur le château du Wa.', 'Vous devez récupérer le bandana de Turorte,qui est détenu par les Wobots dans le donjon du Wa Wabbit.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('937', 'Couture à haut risque.', 'Il faut trouver un moyen de rendre au Bandana Troué son éclat et son état d\'antan. Un Tailleur pourra vous aider.', '198000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('521', 'Récupération mouvementée.', 'Vous avez trouvé l\'espion à temps mais les Brâkmariens ne comptent pas vous laisser les documents. Il vous faut les amener à Amayiro coûte que coûte.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('952', 'Petite blague entre ennemis.', 'Les Brâkmariens devaient remettre les documents à leur supérieur. Vous en avez rédigés des faux pour les remplacer et entraîner vos ennemis sur une fausse piste.', '212000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('520', 'Course contre la montre.', 'Un espion bontarien est en train de fuir Brâkmar par l\'Est de la route. Il faut le retrouver et l\'escorter. Le temps vous est compté !', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('920', 'Rapport protégé.', 'Un rapport vous accablant de reproches serait actuellement rangé dans la Tour des Ordres de Bonta. Vous devez impérativement mettre la main dessus pour prouver votre innocence.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('472', 'Pour vivre,il faut des vivres.', 'Sous la pression du conseil,Amayiro vous a retiré l\'enquête et vous êtes désormais de corvée de ravitaillement.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('921', 'Vol à la tour.', 'Trouvez un complice pour détourner l\'attention du garde pendant que vous effectuerez le larcin.', '85000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1087', 'Usine à jouets.', 'D\'abominables créatures ont cambriolé l\'entrepôt du Père Nowel. Vous devez récupérer les caisses de cadeaux qu\'elles ont volées,avec ou sans leur consentement,et les apporter à Félix Nowel.', '0', ' 1000', '10920;1', '0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('720', 'Le tabi de Grace.', 'Grace Malcean,dans Bonta,a des problèmes avec son tabi.', '25000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('919', 'Un Eca ça flippe énormément.', 'Vous pouvez à présent fabriquer des clefs et inspecter le repaire des bandits à la recherche d\'indices. Une corde d\'escalade ne sera pas de trop pour descendre dans le repaire.', '80000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('916', 'Minou minou.', 'Vous devez mettre la main sur la clef du repaire,ce qui induit forcément de mettre la main sur un bandit la possédant. L\'ex-amant d\'Hersa devrait faire l\'affaire.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('917', 'Clef,sans molette.', 'Vous devez faire des copies de la clef du repaire des bandits avant de pouvoir y pénétrer à plusieurs et d\'y récupérer tout document relatif à l\'enquête.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('715', 'Activités illicites.', 'Des évènements louches se trament dans le quartier des boulangers. Amayiro vous a demandé d\'enquêter.', '17000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('689', 'Entraînement au bâton.', 'Amayiro vous a demandé vingt bâtons de boisaille pour entraîner ses troupes.', '4000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('718', 'Une requête de Danathor.', 'Danathor vous a envoyé une lettre pour vous demander assistance.', '11500', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('719', 'Larry Poller.', 'Larry Poller a demandé votre aide. Il se trouve près de la tour de Bonta.', '11500', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1035', 'Percer le secret de la statue.', 'Percimol a enfoui son secret au pied de la statue. Penchez-vous pour le ramasser.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('751', 'Aider Raaga.', 'Trouvez Raaga dans les mines du nord et demandez-lui son rapport.', '13500', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('738', 'Le rapport de l\'espion.', 'Oto vous a demandé de trouver l\'espion Finche Aticus,à Bonta,et de lui demander son rapport.', '5200', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('743', 'A la recherche de poison magique.', 'Vous devez trouver quelqu\'un dans Brâkmar qui sache préparer des poisons magiques.', '8200', '0', ' 0', ' 0', ' 0', '0');
