INSERT INTO `quetes_objectifs` VALUES ('784', 'A coeur vaillant,rien d\'impossible.', 'Vous devez trouver un membre de la famille Terra et l\'emmener auprès de Jerhyn Gholein. Setag,le maître de classe Enutrof,pourra sans doute vous aider.', '200000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('956', 'Ordre ou contrordre ?', 'Le plan d\'attaque actuel n\'étant pas confirmé,vous devez trouver sa confirmation ou ses modifications auprès de l\'officier Kapi Taigne et rapporter ces informations à Amayiro.', '164000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('510', 'Plan d\'attaque.', 'Amayiro craint un assaut imminent des Brâkmariens. Vous devez trouver leur plan d\'attaque pour permettre à Amayiro de leur préparer un comité d\'accueil digne de ce nom.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('358', 'Entraînement', 'Il faut faire perdre toute sa paille à un épouvantail animé.', '25', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('755', 'Du bois pour Oto.', 'Oto veut 10 écorces d\'abraknyde pour faire du bois de chauffage.', '16300', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('946', 'Engagez-vous,qu\'il disait.', 'Snori Nairb vous a fait entrer illégalement dans la milice de Bonta. Pour officialiser la chose,vous devez rendre visite à Amayiro.', '0', ' 0', ' 0', ' 0', ' 0', '0');
