INSERT INTO `quetes_objectifs` VALUES ('726', 'Désosser Mak.', 'Battez Mak Gahan le Chafer et amenez un de ses os à Oto Mustam.', '50', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('536', 'Attraper l\'espion.', 'Un espion bontarien s\'est infiltré dans la tour et a volé des documents. Il faut utiliser un Zaap pour l\'intercepter,à l\'Est de la route menant à Bonta,du côté de Brâkmar.', '212000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('387', 'Ca roule pour les Roublards.', 'Vous avez maintenant une idée très précise de la cachette des Roublards. Reste à vérifier vos informations.', '2000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('386', 'La crypte,c\'est chic.', 'Vous devez visiter les cinq cryptes du cimetière d\'Amakna.', '3000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('388', 'Pour quelques Roublards de plus...', 'Vous devez vous inflitrer chez les Roublards et aller parler à Vil Smisse.', '15000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('482', 'En territoire ennemi.', 'Vous devez vous infiltrer dans le bâtiment de la milice de Bonta et découvrir où vos ennemis entreposent leurs armes.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('483', 'Une bien belle grosse caisse.', 'Vous devez tromper la vigilance du gardien pour lui prendre les clefs de l\'entrepôt. Il vous faut ensuite dérober une caisse d\'équipement et l\'apporter à Mulic Bere pour expertise.', '116000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('744', 'Les capes brâkmariennes.', 'Oto vous a demandé cinq capes brâkmariennes. Il peut vous indiquer comment les fabriquer.', '8800', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('350', 'Histoire d\'eau', 'Tenva Lakruche a besoin d\'eau pour ses nymphettes.', '75', '25', '0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('529', 'Recherches pour une corne.', 'Le soi-disant gardien de la Corne Edfrit serait dans la jungle profonde de l\'île de Moon. D\'après les rumeurs,vous devez porter une Fourbacoiffe et avoir du Gloutoburg pour qu\'il apparaisse.', '177000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('699', 'Les informations du capitaine.', 'Vous devez trouver un moyen pour entrer en communication avec feu le capitaine Tsog,afin d\'obtenir les informations qu\'il doit faire passer. Le capitaine Alrok connait l\'emplacement de la tombe de Tsog.', '9400', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('710', 'Trouver le coupable.', 'Trouvez l\'assassin de Rimaraf et amenez le formulaire rempli à Amayiro.', '14900', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('711', 'Fausses preuves.', 'Trouvez un moyen de berner Amayiro en lui montrant des fausses preuves. Danathor pourra sans doute vous y aider.', '14900', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('725', 'Le sang des rats.', 'Amayiro vous a demandé d\'anéantir quatre Rats d\'Hyoactif dans les égoûts de Bonta.', '40000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('572', 'Des personnes de confiance.', 'Amayiro a besoin de personnes de confiance pour participer à la mise en scène des faux pouvoirs de la Corne Edfrit.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('573', 'Une mise en scène minutieuse.', 'Pour finaliser la mise en scène,Amayiro a besoin de Miteters présentes sur des Craqueleurs et de la Graisse de Gelée Royale,n\'importe laquelle fera l\'affaire.', '233000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('142', 'Encore de la collecte...', 'Les Crâs tirent leur épingle du jeu,ou plus précisement,leurs flèches. Les Araknes malades semble contenir dans leurs pattes de quoi faire du poison... business is business...', '750', '75', '0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('23', 'L\'appel du fou', 'Eilof demande une pelle de boisaille pour construire son abri anti-apocalypse.', '4000', '50', '0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('27', 'Du pain sur la planche', 'Pour construire sa cabane indestructible,Eilof demande deux planches de Châtaignier,ainsi que quelques pains pour survivre au moins 3 jours au désastre...', '8000', '450', '0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('538', 'Ratatouille.', 'Il faut réunir les ingrédients nécessaires dans le chaudron primordial : un objet de chaque type et un alchimiste,un boulanger,un paysan,un boucher,un poissonnier.', '219000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('537', 'L\'art de cuisiner sans se fatiguer,ou presque.', 'Vous devez trouver les recettes des ingrédients listés sur le papier remis par Oto Mustam.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('936', 'Nos amies les bêtes.', 'Amayiro souhaite en savoir plus sur Turorte. Oshimo,le gardien du Chanil,détient sûrement des informations sur cette tortue.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('516', 'Lenseignements auplès des Lenalds.', 'Turorte a côtoyé et défendu les Lenalds par le passé. Les Lenalds,au sud de l\'île des Wabbits,s\'en souviennent peut-être encore.', '191000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('762', 'Récolte de caleçons.', 'Vous devez vaincre des miliciens de Bonta et... récupérer leurs caleçons. Oto en veut cinq.', '31000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('102', 'Bois d\'Arash', 'Procurez-vous du bois d\'Arash : en sacrifiant la nature,vous pourrez vous lier avec elle.', '1200', '0', '6853,1', '0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('104', 'Rituel', 'Trouvez la personne qui vous aidera à confectionner votre bâton d\'initié.', '1800', '0', '6782,1', '0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('333', 'Montrer ses talents', 'Vos talents se développent mais tout le monde n\'en est pas convaincu.', '370', '0', '8536,1', '0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('514', 'C\'est l\'or,Moon.', 'Le soi-disant gardien de la Corne Edfrit serait dans la jungle profonde de l\'île de Moon. D\'après les rumeurs,vous devez porter une Fourbacoiffe et avoir du Gloutoburg pour qu\'il apparaisse.', '177000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('453', 'De la bave pour confectionner un bijou ?', 'Vous devez récupérer des matériaux pour confectionner un joyau : 5 Lingots d\'Or,1 Bave du Bouftou Royal et 1 Nacre. La Nacre s\'obtient en polissant 10 Nacres brutes que l\'on trouve sur les crustacés et mollusques de l\'île d\'Otomaï.', '80000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1063', 'Usine à jouets.', 'D\'abominables créatures ont cambriolé l\'entrepôt du Père Nowel. Vous devez récupérer les caisses de cadeaux qu\'elles ont volées,avec ou sans leur consentement,et les apporter à Félix Nowel.', '0', ' 1000', '10920,1', '0', ' 0', '0');
