INSERT INTO `quetes_objectifs` VALUES ('365', 'La clef entre les dents', 'Si tu veux la clef du donjon,il faut battre un gros bouftou hargneux.', '55', '0', '8545,1', '0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('508', 'Sadikoi ?', 'Danathor a encore besoin de vos services pour mettre la main sur le grimoire du bwork mage des cavernes réputé pour avoir lancé l\'envoûtement de la Grafioze.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('933', 'Sadidarne.', 'Touh Flowa vous a indiqué où se trouve l\'entrée de la prison végétale. Comme il avait l\'esprit quelque peu embrumé,il faut espérer qu\'il n\'a rien omis de vous dire.', '158000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('732', 'Une clé sur un écureuil.', 'Vous devez retrouver un écureuil dans Brâkmar : il a volé une clé importante. Rapportez-la à Oto.', '2000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('731', 'Amener les caisses.', 'Il faut amener à Oto les caisses d\'armes blanches qui se trouvent dans la tour de Brâkmar.', '1500', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('457', 'Des getas pour Milone.', 'Pour vous punir de ne pas avoir pu empêcher les voleurs de dérober le bijou,Milone vous a demandé de lui amener des Getas Feudala.', '90000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('730', 'Redésosser Mak Gahan.', 'Mak Gahan le Chafer veut sa revanche.', '1000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('502', 'Lenglad en prend pour son grade.', 'William Lenglad a de nouveau besoin d\'une leçon.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('929', 'Tac et Tic.', 'Vous devez trouver un tacticien et le convaincre de rejoindre les rangs de Bonta.', '134000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('481', 'Kaffra Kyper.', 'Oto Mustam veut avoir l\'avis de Kaffra Kyper pour son offensive contre Bonta.', '110000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('480', 'Retour vers Oto.', 'La bataille est terminée,Oto Mustam attend votre rapport.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('479', 'Escarmouche.', 'Un combat a actuellement lieu près de la Baie de Cania,sur la route à mi-chemin entre Bonta et Brâkmar. Il faut le rejoindre et aider Brâkmar à vaincre !', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('735', 'Entraînement au marteau.', 'Oto a besoin de dix Marteaux de Boisaille pour l\'entraînement de ses troupes.', '3500', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('896', 'Le cristal sombre.', 'Vous devez prendre le passage secret dans l\'atelier des alchimistes avec des maîtres dans leur métier. Il vous faut en plus une Potion de Rafale,une Harmonie et une Kobalite.', '85000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('894', 'Le crime ne paie pas.', 'Le message a été envoyé au Captain Starky. En attendant sa réponse,vous devriez aller voir si Sabine s\'est tenue tranquille.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('893', 'La rançon.', 'Sabine étant sous bonne garde,vous devez trouver un moyen de contacter le Captain Starky pour exiger un Cristal de Kain Dharyn comme rançon.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('953', 'Conduite en prison.', 'Vous devez emmener Sabine de force devant Oto Mustam. Vous devez faire attention à ce qu\'elle ne s\'échappe pas à la première occasion.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('455', 'L\'enlèvement de Sabine.', 'Vous devez enlever Sabine et la mettre dans un endroit d\'où elle ne pourra pas s\'échapper.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('895', 'Le crime va payer,foi de Brâkmarien.', 'Les Bontariens ont essayé de délivrer Sabine. Ca ne se passera pas comme ça ! Vous devez leur montrer qui commande.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('754', 'Défier Puja.', 'Vous êtes censé défier Puja Mustam,le fils d\'Oto,et perdre. Mais vous n\'allez quand même pas vous laisser tuer par ce mioche ?', '15600', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('729', 'Un nouveau dentier.', 'Vous devez arracher des dents de Bouftou pour confectionner un dentier. Vous devez en amener exactement assez pour faire un dentier d\'humain... enfin,d\'humanoïde.', '500', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('749', 'Le grand épluchage.', 'Vous devez éplucher des patates pour le dîner. La cuisine se trouve en bas de la milice.', '12100', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1062', 'Vaincre le Manitou Zoth.', 'Vous devez vaincre le Manitou Zoth et apporter une preuve de votre victoire à Bob l\'Enutrof.', '0', ' 0', '10899,1', '0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('515', 'Amadoudoudou c\'est bon.', 'Il faut trouver un moyen d\'amadouer Turorte pour mettre la main sur la Corne Edfrit. Les résidents de l\'île de Moon peuvent vous aider.', '184000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('489', 'Retour vers Oto.', 'Le tacticien ayant quitté la ville,vous pouvez raisonnablement laisser penser à Oto Mustam que vous l\'avez tué.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('908', 'Dompteur de bêtes,mais aussi d\'animaux.', 'Oto Mustam veut connaître le secret du domptage des crocs glands. Vous devez retourner à la bibliothèque de Bonta pour y trouver des indices.', '134000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('899', 'Les andouillettes d\'une andouille.', 'Oto Mustam étant persuadé que vous cherchez à le tromper,il faut impérativement retrouver le joyau avant d\'être transformé en andouillettes.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('460', 'Victoire !', 'Xephirès a accepté les conditions et est prêt à trahir Bonta le moment venu. Oto sera satisfait d\'apprendre la nouvelle.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('459', 'L\'entretien.', 'Tout est prêt,il ne reste plus qu\'à retrouver Xephirès à la Taverne du Pandawa Ivre pour lui remettre la lettre et fixer un rendez-vous pour sa réponse.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('900', 'Vengeance,mouahaha.', 'Le repaire des bandits est piégé. Il vous faut trouver de l\'aide pour vous accompagner et vous aider à désamorcer les pièges protégeant leur cachette.', '100000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('497', 'Au rapport.', 'La bataille est terminée,Jerhyn Gholein attend votre rapport.', '110000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('496', 'Escarmouche.', 'Un combat a actuellement lieu près de la Baie de Cania,sur la route à mi-chemin entre Bonta et Brâkmar. Il faut le rejoindre et aider Bonta à vaincre !', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('727', 'Offre d\'emploi.', 'Oto Mustam veut savoir qui a assassiné le patron de la Taverne du Chabrulé.', '125', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('742', 'La bouillie de blops.', 'Il faut apporter de la bouillie de blop concassés à Oto. Il peut vous donner la recette.', '7600', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('745', 'Trouver le testament.', 'Vous devez trouver le testament d\'Oto dans la tombe de son père,dans le cimetière - elle se reconnait par la gerbe de fleurs qui l\'entoure. Vous devez être transformé en esprit pour y entrer.', '9400', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('495', 'Pirate à quai.', 'Grâce à Xephirès,vous savez que le navire bontarien La Descemer transporte de nombreux objets magiques dans ses cales. Vous devez le trouver et faire main-basse sur sa marchandise.', '164000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('926', 'Liquidation totale.', 'Une Sram doit de l\'argent à Oto Mustam. Comme elle ne l\'a pas remboursé,il a décidé de l\'éliminer à titre d\'exemple. Vous pourrez probablement la trouver dans une salle de jeux clandestine.', '233000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('544', 'Equiper les guerriers.', 'Oto craint une attaque de Bonta et vous a demandé d\'équiper certains des guerriers de Brâkmar avec de l\'équipement spécial.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('927', 'Copinage.', 'Kosuke Ichazum avait raison : Latem Sa a obtenu le contrat pour équiper la milice de Bonta par copinage et non pour ses compétences de forgeron.', '116000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('498', 'Forger avec ses pieds.', 'Vous devez enquêter sur le fournisseur de l\'équipement de la milice et mettre à l\'épreuve le forgeron qui le fabrique.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('902', 'Le puits du fou.', 'Vous devez trouver l\'emplacement du Puits des Âmes Souillées afin de révéler le plein potentiel des pierres de souillure. Le maître de l\'Ordre de l\'Esprit Malsain,Divad Dleifrepok,pourra vous aider.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('901', 'Alchimie malfaisante.', 'Vous devez vous procurer des pierres de souillures. Seuls les mineurs sont capables d\'en fabriquer à partir des créations brutes d\'un alchimiste déjanté.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('903', 'Sept âmes sinon rien.', 'Vous devez capturer les âmes de sept bandits et les amener au Puits des Âmes Souillées pour les asservir. Vous aurez besoin de Brâkmariens vils au point de pouvoir se choisir une spécialité.', '105000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('533', 'Retrouver l\'espion.', 'Vous devez rapidement retrouver l\'espion blessé sur la route menant à Bonta,et récupérer les documents. L\'espion doit être plus proche de Bonta que de Brâkmar.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('463', 'Tournée des tavernes.', 'Le bandit à qui appartenait l\'épée est probablement passé dans l\'une des tavernes. Par conséquent,les taverniers doivent savoir quelque chose.', '65000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('733', 'Enquête importante.', 'Faites le tour des gardes de Brâkmar et réalisez un compte-rendu des gardes qui ne prennent pas de pause.', '2500', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('909', 'Dompteur de bêtes,mais aussi d\'animaux.', 'Oto Mustam veut connaître le secret du domptage des crocs glands. Vous devez retourner à la bibliothèque de Bonta pour y trouver des indices.', '134000', '0', ' 0', ' 0', ' 0', '0');
