INSERT INTO `quetes_objectifs` VALUES ('898', 'La confirmation.', 'Pandalida a profité de votre combat pour s\'enfuir,elle s\'est sans doute réfugiée chez elle. Vous DEVEZ la convaincre de remettre la lettre à Xéphirès.', '95000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('897', 'Intimidation intime.', 'Il faut intimider Pandalida en saccageant sa maison. Vous avez besoin de 3 Pâtes à Tra Khben,pâte obtenue en concassant 15 Groins du Sanglier,5 Pots de Gloutovore et 1 Plume de Serpiplume.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('458', 'Le facteur.', 'Il faut trouver un moyen de faire passer la lettre à Xephirès. Il est souvent à la taverne du Pandawa Ivre mais refuse désormais de vous parler.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('739', 'La relève.', 'Vous devez trouver le guerrier Tink Berval et lui demander de relever Brank Thoof.', '5800', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('912', 'Le tort tue.', 'Turorte ayant faim et la manière forte étant un échec,il faut trouver un moyen de l\'empoisonner. Un habitant de l\'île connaît sûrement l\'alimentation des tortues.', '184000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('530', 'Mettre la tortue sur le dos.', 'Puisque vous n\'arrivez pas à soutirer des informations juste en demandant,il va falloir y aller d\'une façon plus directe...', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('352', 'Transformation', 'Poss\'Ybel veut que vous lui fournissiez quelques produits finis issus du fruit de vos récoltes.', '50', '15' ,'0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('378', 'Transformation', 'Poss\'Ybel veut une mini potion de soins faite d\'une fleur de lin et d\'un peu d\'eau.', '50', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('379', 'Transformation', 'Poss\'Ybel veut que vous lui fournissez quelques produits finis issus du fruit de vos récoltes.', '50', '15' ,'0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('380', 'Transformation', 'Poss\'Ybel veut que vous lui fournissez quelques produits finis issus du fruit de vos récoltes.', '50', '15' ,'0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('686', 'Traquer les buveurs.', 'Vous devez faire le tour des tavernes de Bonta pour trouver les miliciens qui ne sont pas à leur poste et leur demander leur badge.', '2500', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('364', 'Conditions d\'échange', 'Anta vous a fait une liste de ce qu\'elle veut en échange de son collier magique.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('347', 'Je fais du social', 'Disskray veut savoir si les villageois n\'ont rien remarqué d\'étrange ou d\'inhabituel', '150', '0', '8535,1', '0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('179', 'Shaman connexion', 'Le shaman est un Pandawa qui en sait beaucoup sur les agissements de certains... Apparemment,tout n\'est pas rose à Pandala !', '3000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('177', 'A la santé de la déesse !', 'Nouvel arrivé à Pandala,l\'Assistant Shaman vous demande de faire honneur à votre déesse : une offrande spiritueuse serait la bienvenue... Votre initiation commence.', '500', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('343', 'Trouver un petit boulot', 'Posteur Ninh a surement une commission à vous confier. ', '50', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('342', 'Une recette pour Pipelette', 'Pipelette attend la recette des crêpes d\'Astrub pour préparer la venue de ses petits enfants. ', '0', ' 75' ,'0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('915', 'La valse des rocheuses.', 'Descendez dans le repaire des bandits à l\'aide d\'une corde. N\'y allez pas seul !', '70000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('465', 'Jolie minette.', 'Votre rencontre avec les bandits vous a appris que Hersa Griffe Sombre en sait plus qu\'elle ne veut l\'admettre. Reste à lui parler sans se faire défigurer.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('464', 'Repaire pépère.', 'Amayiro vous a demandé d\'enquêter dans les plaines de Cania pour trouver le repaire des bandits. D\'après vos informations il serait non loin de la foire du trooll,un peu à l\'Ouest.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('928', 'Pretty Nara.', 'Nara vous a fournit la longue liste de fournitures dont il a besoin pour forger l\'équipement de la milice de Bonta. Vous devez les lui amener dans la Mine des Porcos.', '128000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('955', 'Agence immobilière.', 'Vous avez visité les différents lieux indiqués par Setag. Reste à convaincre Nara de déménager dans l\'un d\'eux.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('501', 'Prospection immobilière.', 'Nara vous a demandé de trouver un endroit isolé où installer sa forge et son matériel.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('695', 'Transport de caisses.', 'Vous devez acheminer quatre caisses entre Amayiro et l\'avant-poste de Bonta,au sud de la cité.', '7000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('329', 'Faire ses preuves', 'Comme vous n\'êtes pas très connu alentours,les autochtones ne savent pas encore s\'ils peuvent vous faire confiance.', '105' ,'0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('367', 'Licence quatre', 'Vous avez la bière,en la donnant à Aymé,vous devriez recouvrer toute votre énergie.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('366', 'Se mettre en bière', 'Aymé veut de la bière,pourquoi ne va-t-il pas la chercher tout seul ?', '15', '10', '0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('531', 'L\'ami des bêtes.', 'Vous devez trouver d\'où vient Turorte. Un spécialiste des animaux pourra vous aiguiller dans vos recherches. Oshimo,responsable du chanil,est un bon point de départ.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('913', 'Le wabbit et la tortue.', 'D\'après Oshimo,Turorte se serait fait un ennemi mortel. Lelievwe Pâle Pitane aurait signé un pacte avec les démons pour avoir plus de puissance. Il réside sur l\'île des wabbits squelettes.', '191000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('361', 'Carnets d\'adresse', 'Ajouter un joueur dans votre liste d\'amis.', '20', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('371', 'Il est à qui le Kiki ?', 'Ma\'Ster vous a mis au défi de battre le monstre tapi dans sa niche.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('320', 'Jeu Trooll règles avancées', 'Raxitik est terrorisé. Il sait que les Bworks vont le livrer au Trooll pour qu\'il pratique une partie de jeu Trooll règles avancées avec lui. Il faut absolument éliminer ce Trooll qui menace l\'intégrité physique de Raxitik.', '30000', '0', '2562,1', '0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('995', 'Pélerinage sanglant.', 'Affrontez le dopeul de chacun des temples de classe.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('765', 'Anéantir les tortues.', 'Oto vous a demandé de tuer dix tortues de l\'île de Moon. Leurs yeux feront une bonne preuve.', '17000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('764', 'Du vin pour Oto.', 'Vous devez amener à Oto du vin de Gloutovore. Où pourrait-on trouver ça ?', '17000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('763', 'L\'entretien.', 'Une personnalité importante de Brâkmar a demandé à vous voir. Elle se trouve dans les sous-sols de la tour de Brâkmar.', '17000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('728', 'Des potions énergétisantes.', 'Oto Mustam a besoin de trois potions Pahoa Raide.', '250', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('449', 'Le repaire repéré.', 'Grâce au message du chef des voleurs vous êtes en mesure de trouver son repaire. Pourquoi ne pas y faire un tour avant de rentrer à la milice ?', '90000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('756', 'Acheter l\'équipement.', 'Vous devez acheter l\'équipement qu\'Oto a indiqué sur sa liste et l\'équiper avant de retourner le voir.', '17000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('761', '32 dents en or !', 'Vous devez apporter un dentier en or pour Mak. Vous pourrez en trouver sur des Chafers d\'élite,mais le forgeron de Brâkmar peut sans doute vous en fournir un aussi.', '28000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('487', 'Pénétrer dans la bibliothèque.', 'Les espions à Bonta ont échoué dans leur assassinat,il faut donc le faire à leur place. La cible est un tacticien qui réside dans la bibliothèque de Bonta mais gare aux chiens de garde.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('486', 'Assassinat.', 'Vous pouvez maintenant entrer dans la bibliothèque pour vous occuper du tacticien.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('750', 'Humilier Sbab ?', 'Oto vous a demandé d\'amener dix oreilles de Tiwabbits à Sbab pour l\'horrifier. Mais il y a peut-être une autre solution si vous ne souhaitez pas le faire.', '12800', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('734', 'Tabasser les Bontariens.', 'Oto vous a demandé une bière de la Tabasse. La taverne de la Tabasse se trouve à Bonta.', '3000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('753', 'Retrouver les documents.', 'Les documents secrets n\'étaient pas sur l\'espion. La faune des landes de Sidimote s\'en est peut-être emparée.', '14900', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('938', 'La Révélation.', 'Sous l\'hôtel de vente des alchimistes il y a une armoire. Il vous suffit de vous placer devant pour ouvrir le passage secret. Le Mage Ax vous attend là-bas.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('526', 'Le Mage Ax.', 'Amayiro vous a demandé d\'interroger le Mage Ax pour connaître le mode d\'emploi de la Corne Edfrit et ses pouvoirs.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('939', 'Retour au bercail.', 'Il est temps de rentrer à Bonta et de faire le point avec Amayiro.', '226000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('882', 'Le savoir basique.', 'Allez apprendre la technique de la capture d\'âme. La clef du donjon doit se trouver sur les créatures de la région.', '35000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('740', 'Les casques de Chafer.', 'Oto veut dix casques de Chafer qu\'on trouve au Cimetière des Torturés.', '6400', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('525', 'Présence brâkmarienne.', 'Vous avez attaqué un Brâkmarien qui était déjà sur place mais qui ne possédait pas encore la Corne Edfrit. Si Ecaflip est de votre côté peut-être trouverez-vous la Corne Edfrit avant les Brâkmariens.', '219000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('524', 'La décision de Turorte.', 'Convaincre Turorte d\'échanger le Bandana Rapiécé contre la Corne Edfrit. Vous avez besoin d\'une Fourbacoiffe et d\'une Bouteille de Gloutoburg pour qu\'elle vous apparaisse.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('741', 'Chasse au loup.', 'Oto vous a demandé de débarrasser Brâkmar d\'un loup qui rôde autour de la façade.', '7000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('500', 'Si près du but.', 'Norb sait où se trouve le forgeron ermite,il vous reste à le convaincre de divulguer sa cachette. Peut-être qu\'Amayiro pourra vous aider.', '122000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('499', 'Ainsi forgeront...', 'Il faut trouver un forgeron compétent qui accepterait de faire de l\'équipement pour Bonta.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('527', 'Une autre histoire de poivrot.', 'D\'après vos sources,le tavernier de Sufokia aurait des informations sur une puissante corne magique.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('910', 'Jamais mieux servi que par soi-même.', 'L\'information révélée par le Tavernier du Ripate n\'est pas exploitable telle quelle. Une recherche auprès des érudits pourrait s\'avérer plus fructueuse.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('911', 'Kokoko,koko ?', 'Pressentant la prochaine mission que vous confiera Oto Mustam,mieux vaut préparer le terrain et acquérir quelques noix de kokoko.', '170000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('528', 'Carapaces.', 'Le tavernier du Ripate vous a demandé une carapace de tortue de Moon de chaque couleur. Mais peut-être allez-vous opter pour une méthode plus rapide.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1055', 'Combattre le lupus minimus.', 'Un combat qui met à l\'épreuve toutes vos capacités.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('890', 'Une mission presque achevée.', 'Le chemin est à présent dégagé,même si les gobelins ne constituaient pas une réelle menace. Exoram va enfin pouvoir vous révéler ce qu\'il sait et Oto Mustam sera fier de vous.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('446', 'Abrakadabra,c\'est encore moi.', 'Exoram n\'est pas rassuré car il a entendu des bruits suspects et des rires narquois plus au nord. Si vous pouvez lui assurer que le chemin est dégagé,il vous donnera les informations prisées sur Xephirès.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('445', 'Abrakadabra,me voilà.', 'Vous avez rapporté des informations utiles à Oto Mustam,qui vous a demandé de prendre la relève d\'un milicien. Vous devez chercher un Sram connaissant Xephirès,dans la forêt des Abraknydes.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('891', 'Une mission loin d\'être achevée.', 'Des traces de lutte et des restes de karne indiquent qu\'Exoram s\'est battu et a été capturé ou tué. Oto Mustam sera furieux de l\'apprendre.', '65000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('444', 'Les bons bars de Bonta.', 'Il faut trouver des informations sur Xephirès. Les taverniers de Bonta pourraient peut-être fournir une piste.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('737', 'La récolte de poison.', 'Oto Mustam a besoin de 20 pods de venin de Scorbute.', '4600', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('485', 'Récolte de poison.', 'Les espions à Bonta ont besoin de poison : un poison rare détenu uniquement par les Bworks Mages. Les espions se cachent dans le quartier sud-ouest de Bonta.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('906', 'La bonne planque.', 'Vous devez trouver une nouvelle planque pour les espions entre leur emplacement actuel et la bibliothèque. Au besoin,vous devez neutraliser ses éventuels occupants.', '128000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('535', 'Un petit échec.', 'Des traces de combat,deux armes laissées au sol,un Sram décédé : tout porte à croire que vous n\'êtes pas arrivé à temps.', '205000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('918', 'Exoram le Sram.', 'Vous avez ramené Exoram au temple Sram. Cette fois-ci il a intérêt à vous donner les informations qu\'il s vous a promis.', '70000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('892', 'Un Sram plus bête que nature.', 'Exoram s\'est encore mis dans de sales draps. Si vous voulez les informations sur Xephirès,il va falloir l\'aider une nouvelle fois.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('447', 'S\'infiltrer ou foncer dans le tas,telle est la question.', 'D\'après vos découvertes,il semblerait qu\'Exoram soit retenu prisonnier dans le campement gobelin sauf s\'il est déjà mort.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('448', 'Sur les traces du Sram.', 'Exoram a été relâché vivant par les gobelins. Vous devez à nouveau partir sur ces traces pour lui mettre la main dessus.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('493', 'Dans la grotte Porcos.', 'Vous devez tenter de rallier le forgeron de la grotte Porcos à la cause de Brâkmar.', '152000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('759', 'Leçon d\'humilité.', 'Oto vous a ordonné de lui amener 40 yeux de larves bleues.', '23000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('760', 'Se débarrasser de la larve dorée.', 'Vous devez trouver un moyen de vous débarrasser de la larve dorée qui vous suit.', '25000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('452', 'Un échec... temporaire', 'Xephirès a refusé l\'offre,mais tout n\'est pas perdu. Un bijou saura l\'amadouer.', '75000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('451', 'La rencontre.', 'Maintenant que l\'endroit où Xephirès se trouve a été identifié,il faut le trouver à la taverne du Pandawa Ivre et lui faire la proposition. Il y est à partir de 16 heures.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('736', 'Un message à faire passer.', 'Oto veut que vous passiez son message à Vil Smisse,dans le repaire des Roublards qui se situe dans le cimetière d\'Amakna.', '4000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('491', 'Nouveau danger qui fait peur.', 'Plusieurs guerriers brâkmariens ont été vaincus et laissés pour morts sur la route menant à Astrub. Il faut aller enquêter là-bas mais avant,Oto Mustam veut une âme de Croc Gland.', '140000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('747', 'Récolter la taxe.', 'Vous devez récolter la nouvelle taxe d\'Oto dans chaque taverne de Brâkmar. Carte blanche pour tabasser les taverniers qui ne veulent pas payer.', '10700', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('889', 'Une lettre classée X.', 'Il vous faut trouver l\'identité de ce fameux X. Là lettre fait référence à un lieu où il a l\'habitude d\'aller. Trouver ce lieu permettra d\'en savoir plus.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('443', 'Retour de la tour.', 'Vous avez trouvé un morceau de papier très intéressant. Il faut le montrer à Oto.', '60000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('442', 'La tour désordre.', 'Trouvez dans Bonta un personnage important qui serait susceptible d\'être corrompu. Oto a suggéré de chercher dans la tour.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('792', 'A la recherche du greffier déprimé.', 'Tabach,Greffier de l\'Ordre du Coeur Vaillant,écume les tavernes depuis la disparition de son chat. Il faut le ramener auprès de Jerhyn Gholein.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('793', 'A la recherche de la greffière.', 'Ybaul,Greffière de l\'Ordre de l\'Esprit Salvateur,écume les tavernes à la recherche de ses frères. Il faut la ramener auprès d\'Elviana Tirips.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('795', 'Os de coeur.', 'Kaffra Kyper vous a demandé de lui apporter 1 Os de Chafer pour lui prouver votre valeur.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('797', 'Mauvais oeil.', 'Ledrob Terceséc vous a demandé de lui apporter 1 Œil de Corbac pour lui prouver votre valeur.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('210', 'A coeur saignant,rien d\'impossible.', 'Vous devez vous emparer des statuettes symboles des biens et des maux de l\'humanité et les remettre à Kaffra. Elles sont cachées dans une mine abandonnée près du temple Enutrof.', '200000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('796', 'Poils au menton.', 'Divad Dleifrepok vous a demandé de lui apporter 1 Poils de Barbe de Bwork Mage pour lui prouver votre valeur.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1071', 'Usine à jouets.', 'D\'abominables créatures ont cambriolé l\'entrepôt du Père Nowel. Vous devez récupérer les caisses de cadeaux qu\'elles ont volées,avec ou sans leur consentement,et les apporter à Félix Nowel.', '0', ' 1000', '10920,1', '0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1113', 'Collecte de céréales.', 'Fidèle à son habitude,Annette Daimpeau est partie dormir au lieu de ravitailler Frigostine. La boulangère vous a donc demandé de remplacer son employée.', '85000', '10000', '11196,1', '0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1114', 'Collecte de fleurs.', 'Ne pouvant laisser son atelier sans surveillance,Eric Azaraille vous a demandé de lui servir de fournisseur.', '85000', '10000', '11196,1', '0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1115', 'Collecte de poissons.', 'Avec l\'arrivée massive d\'aventuriers,les réserves de poisskailles diminuent à vue d\'oeil. C\'est pourquoi Clarisse Tosha vous a demandé de la ravitailler.', '85000', '10000', '11196,1', '0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1117', 'Collecte de minerai.', 'L\'exploitation des mines a pratiquement cessé depuis plusieurs décennies,et les réserves d\'obsidienne servant à la fonte d\'alliage sont à sec. Laurent Gebleuh vous a donc proposé de l\'aider à renflouer ses stocks.', '85000', '10000', '11196,1', '0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1116', 'Collecte de viandes.', 'Avec l\'arrivée massive d\'aventuriers,les stocks de viandes diminuent à vue d\'oeil. C\'est pourquoi Al Shab vous a demandé de le ravitailler.', '85000', '10000', '11196,1', '0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1118', 'Collecte de bois.', 'Le Chef Rhonté a besoin d\'une quantité astronomique de bois pour servir de combustible au système de dégel du port. Il vous a demandé un petit coup de main.', '85000', '10000', '11196,1', '0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1107', 'Chasse aux boufmouths.', 'Clyde Parker vous a demandé de vous débarrasser des boufmouths,dans les champs de glace.', '170000', '0', '11196,1', '0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1108', 'Chasse aux créatures de la forêt.', 'Clyde Parker vous a demandé de vous débarrasser des créatures de la forêt,dans la forêt des pins perdus.', '201000', '0', '11196,1', '0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1109', 'Chasse aux mansots.', 'Clyde Parker vous a demandé de vous débarrasser des mansots,dans le lac gelé.', '223000', '0', '11196,1', '0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1110', 'Chasse aux fantômes de pirates.', 'Clyde Parker vous a demandé de vous débarrasser des fantômes de pirates,dans le berceau d\'Alma.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1111', 'Chasse aux crapeurs.', 'Clyde Parker vous a demandé de vous débarrasser des crapeurs,dans les larmes d\'Ouronigride.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1112', 'Chasse aux créatures de lave.', 'Clyde Parker vous a demandé de vous débarrasser des créatures de lave,dans les tunnels de lave.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1121', 'Récompense méritée.', 'Reparler à Grand-père et lui montrer les restes de rat bougri.', '80000', '3000', '0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1120', 'La cave infestée.', 'Aller dans la cave de Grand-père et exterminer la menace.', '20000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1175', 'Mansoétude.', 'Craignant son extinction,Ken Tucky souhaite recuellir des mamansots afin de préserver l\'espèce.', '892000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1173', 'Trouver et ramener la créature.', 'Certains prétendent avoir vu Monsieur Pingouin au milieu des mansots. Il est recherché pour vol de poisskaille et dégradation volontaire de pêcheur.', '0', ' 260000', '11196,1', '0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1172', 'Trouver et ramener la créature.', 'Certains prétendent l\'avoir aperçue en compagnie de fantômes. Elle est recherchée pour vol d\'un masque d\'argent et agressions répétées sur des furets domestiques.', '0', ' 320000', '11196,3', '0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1171', 'Trouver et ramener la créature.', 'Certains prétendent avoir été attaqués près des sources thermales des larmes d\'Ouronigride. La Vengeuse Masquée est recherchée pour trouble de l\'ordre public et vengeance injustifiée.', '0', ' 350000', '11196,3', '0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1170', 'Trouver et ramener la créature.', 'Certains disent l\'avoir vu se diriger vers la Crevasse Perge après l\'un de ses forfaits,au nord de l\'île. Il doit être mis en cage à la Bourgade pour ses expéditions carnassières envers les Boufmouths d\'élevage.', '0', ' 380000', '11196,3', '0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('334', 'Ailes en kit', 'Gobriel en a assez que les autres Gobelins lui jettent des pierres à cause de la couleur de sa peau. Il voudrait devenir un ange.', '30000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('351', 'Le fil de l\'histoire.', 'Pour apprendre l\'histoire de l\'épée de Iop vous pouvez aller voir Tenva au temple.', '75', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('348', 'Bienvenue', 'Bienvenue', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('349', 'Piou', 'Piou piou piou piou piou !', '250', '0', '6897,2;8537,1', '0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('346', 'Arietta Fiolle', 'Allez voir où se trouve Arietta Fiolle.', '25', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('332', 'Tonte matinale', 'Jicé est un type génial,il a compris qu\'avec de la laine on pouvait faire... de la laine', '120', '150', '0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('344', 'Encore une livraison', 'Cette fois il faut porter un objet à un Xelor qui s\'occupe d\'un Zaap.', '150', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('416', 'Bunny suit', 'Le pwince d\'Wa vous a demandé de prendre sa place pour démasquer les assassins. Encore faut-il trouver quelqu\'un qui puisse vous tranformer en Wabbit.', '15000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('415', 'Au meurtre!', 'Le pwince d\'Wa se fait attaquer,protégez-le!', '5000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('417', 'Courses', 'Maintenant que la formule est complète,il faut les ingrédients.', '120000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('390', 'Dilemme dilemme..', 'Vous savez maintenant qu\'Yse Vewybad complote contre le Roi. Il va falloir prendre parti pour l\'un ou l\'autre.', '2000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('254', 'Un bon repas', 'Lorkos vous offrira une clef du labyrinthe si vous lui rapportez de quoi préparer un bon repas.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('542', 'Disparition.', 'Vous pouvez à présent finir vos recherches et faire votre rapport à Oto Mustam.', '226000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('539', 'Le côté obscur du wabbit.', 'Vous devez négocier l\'emplacement de la Corne Edfrit contre un Grattoir à dos Spontex auprès de Turorte,mais qui c\'est ce qui vous attend dans la jungle profonde de Moon ?', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('540', 'La fourfouille.', 'Turorte vous a révélé que la Corne Edfrit était enterrée au nord-est de l\'île,près d\'une statue de phénix.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('541', 'Le trouble-fête.', 'Vos fouilles ont été interrompues par l\'arrivée d\'un Sadida. A vous de lui montrer ce qu\'il arrive lorsqu\'on ose s\'opposer à Brâkmar.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('260', 'Cavalerie légère', 'Fournir quelques montures fraîches pour la milice.', '75000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('914', 'Spentox,ça gratouille et ça chatouille.', 'Mulic Bere vous a donné accès à sa forge spéciale pour fabriquer un grattoir à dos Spentox.', '198000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('532', 'La forge de luxe.', 'Il faut fabriquer un grattoir à dos que Turorte puisse utiliser. Etant le meilleur forgeron de Brâkmar,Mulic Bere pourra vous aider à réaliser cet outil particulier.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('746', 'La dèche d\'Oto.', 'Oto n\'a plus un sou. Comment y remédier ?', '10000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('748', 'Voler le Poumon.', 'Oto veut que vous voliez le Poumon de la plaine à Azra Lazarus,la bijoutière. Il semble qu\'elle ait un garde,mais peut-être est-il possible d\'éviter de le combattre...', '11400', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('758', 'Trouver un Bontarien pas net.', 'Vous devez trouver un Bontarien coupable d\'assassinat. Pour cela,il faut se procurer un bâton renifleur à Fryda,puis l\'essayer sur des Bontariens jusqu\'à en trouver un qui convienne.', '21000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1094', 'Gisgoul,ma patrie.', 'L\'ambition des Brâkmariens n\'a pas de limite.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1096', 'Mauvais plan.', 'Allez semer des petits caillous blancs dans le labyrinthe du Dark vlad.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1095', 'L\'Orc qui tu rie,te saluant.', 'Faites les courses de Léthaline.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1097', 'Goultard-Z', 'Toutes les pièces sont placées,il faut porter le dernier coup.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1098', 'Gisgoul,ma patrie.', 'L\'ambition des Brâkmariens n\'a pas de limite.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1101', 'Goultard-Z', 'S\'échapper du traquenard Brâkmarien.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1099', 'La théorie du chaos.', 'Trouver la source du mal.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1100', 'Mauvais plan.', 'Contrecarrez les plans des Brâkmariens.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1103', 'Certification.', 'Allez voir l\'Alchimiste Examinateur,faisant actuellement des recherches sur le calcium du cartilage intranasal post-mortem.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1102', 'Alch\'ti mi.', 'Nibé Lulle vous a indiqué où trouver le chef des instructeurs pour commencer votre formation d\'alchimiste.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('461', 'Renseignements à la Bagrutte', 'Le marchand de vin local a disparu. Le tavernier de la Bagrutte a peut-être des renseignements.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('462', 'Enquête à l\'Est.', 'D\'après le tavernier Bagrutte,le marchand avait l\'habitude de rentrer chez lui,un peu plus à l\'Est.', '60000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('512', 'Sufokia.', 'Une rumeur affirme qu\'il existe une corne disposant de puissants pouvoirs magiques. Les habitants de Sufokia auraient des informations supplémentaires.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('513', 'Carapaces de tortue.', 'Le tavernier du Ripate veut que vous lui apportiez des carapaces de tortue de Moon,une de chaque couleur,mais peut-être qu\'une grosse somme de Kamas pourrait les remplacer.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('935', 'Kokoboy.', 'Mieux vaut être prévenant et acquérir un stock de Noix de Kokoko conséquent avant de retourner auprès d\'Amayiro.', '170000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('934', 'Une corne à la mer.', 'Selon le Tavernier du Ripate la corne se trouverait sur une île. Des érudits pourront sans doute vous renseigner davantage.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('697', 'Enquête sur la foire.', 'Il faut chercher un formulaire auprès de Ture Inge,dans la milice,puis interroger le chef de la foire du Trool afin de détecter s\'il est un Brâkmarien ou non.', '8200', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('171', 'S\'évader c\'est la santé ', 'Apparemment,vous n\'êtes pas le bienvenu à Aerdala... saurez-vous échapper à votre destinée ? Il vaudrait mieux...', '10000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('169', 'Zero Dofus', 'Le messager du sage Lenald vous envoie à la lisière du village d\'Aerdala. Sur le chemin,vous méditez ses derniers mots : \"Plus d\'un aventurier s\'est fait mal accueillir en allant à Aerdala. On les dit alliés des Pandawas rebelles,ceux qui sabotent tous les accès de l\'île...\" ', '2000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1041', 'Miaou.', 'Oshimo exige un chacha en échange de son secret pour accéder au Sanctuaire des Familiers.', '2500', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1141', 'Tique psychodyslep.', 'Wogew l\'hewmite pense qu\'Otomaï peut l\'aider dans ses recherches et vous envoie lui apporter des documents pour se faire.', '1020000', '5000', '11196,1', '0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1140', 'Thérapeutique.', 'Vous devez trouver une solution à l\'invasion de tiques qui parasitent les boufmouths.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1119', 'C\'est l\'heure de l\'apéwicube.', 'Vous avez trouvé une Plume Vibrante de mansot,mais impossible de connaître son utilité. Heureusement,vous avez entendu certains pêcheurs en parler en citant Kyrie Lawache. Peut-être pourra-t-elle vous en dire plus.', '5000', '0', '11134,1', '0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1127', 'Graines des champs.', 'Afin de créer des espèces de céréales plus résistantes au froid,Cassiopée Hisane vous a demandé de lui apporter plusieurs types de graines.', '510000', '10000', '11196,1', '0', ' 0', '0');
