INSERT INTO `quetes_objectifs` VALUES ('94', 'Livraison', 'Apportez le remède que vous avez obtenu à Clint Pot. Pour soigner les Tofus ou les Enutrofs,je n\'ai pas bien compris. N\'oubliez pas de faire un rapport à votre maître de guilde.', '1800', '0', '6785,1', '0', ' 0', '0');
