INSERT INTO `quetes_objectifs` VALUES ('145', '... de ciseaux', 'Avec les ciseaux tout neufs que vous avez récuperer,c\'est Phite qui va etre contente !', '1750', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('133', 'Une belle paire...', 'Phite Pouque cherche des ciseaux forgés,elle conseille d\'aller voir le forgeron de l\'hotel des ventes pour lui en acheter...', '2000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('134', 'Des cheveux... toujours des cheveux...', 'Phite cherche à récupérer la totalité des types de cheveux des habitants d\'Astrub,pour en faire des postiches.', '3000', '150', '0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('135', 'Un début de piste', 'Les Enutrofs partis à la recherche des ranifftous se sont  probablement égarés loin dans les profondeurs des galeries. Les lieux sont infestés de milirats d\'égoutants...', '4000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('146', 'Un tien vaut mieux que 2 pods de rat', 'Débarrassez-vous de ce Rat d\'égoutant... il a l\'air malade,ça ne devrait pas être trop compliqué... enfin,à plusieurs quoi.', '8000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('139', 'Le début de la fortune', 'Collecter 10 pattes d\'Araknées pour le compte de Ant Emicheun. Ou pour le votre... qui sait ? En tout cas,ça semble rentable.', '1000', '400', '0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('140', 'Plumes malades', 'Avec vingt plumes,on doit pouvoir faire un plumeau. Ceci dit,vu les plumes...', '1000', '50', '0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('148', 'Récompense méritée', 'Reparler à grand mère et lui montrer la tête de sousouris.', '40', '30', '0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('147', 'La cave infestée', 'Aller dans la cave de grand-mère et exterminer la menace.', '10', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('679', 'Battre le milicien.', 'Vous devez battre Tarche le milicien,dans la salle à côté.', '50', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('681', 'Donner des pains.', 'Vous devez apporter 20 pains au blé complet à Amayiro.', '250', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('682', 'Les dents de Bouftou.', 'Vous devez arracher des dents de Bouftous,exactement le nombre constituant un dentier humain,et les amener à Ned,au sud-ouest de la ville.', '500', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('683', 'Une tatanne pour le frérot.', 'Vous devez battre le milicien Torche et ramener une preuve à Amayiro.', '1000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('685', 'Retrouver le Corbac.', 'Vous devez retrouver le Corbac qui a volé le collier de la fille du maire,le battre et récupérer le collier. Le Corbac serait vers la tour.', '2000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('688', 'Chasse au sanglier.', 'Vous devez trouver le sanglier agressif au sud de Bonta et le battre.', '3500', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('691', 'La réparation du katana.', 'Vous devez demander à Kosuke le forgeron de réparer la lame d\'Amayiro en échange du chèque.', '4600', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('690', 'Tabasser le mauvais payeur.', 'Le tavernier Tabasse doit une forte somme à Amayiro : vous devez le convaincre par tous les moyens de la payer.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('692', 'Communiquer avec l\'espion.', 'Vous devez trouver Bishom Pot dans Brâkmar et lui soutirer les informations qu\'Amayiro a demandées.', '5200', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('694', 'Colmater la brèche.', 'Shere Hacanon,à l\'entrée de Bonta,a besoin de quatre pierres de Craqueleur des plaines pour colmater la brèche des murailles.', '6400', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('696', 'Collection de casques.', 'Vous devez amener 15 casques de Chafers d\'Elite à Amayiro.', '7600', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('698', 'De la bouillie pour le Tabi.', 'Apporter de la bouillie de fleurs de blops à Amayiro. Pour obtenir cette bouillie,il faut concasser quatre fleurs de blops de couleurs différentes dans un Concasseur Munster.', '8800', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('700', 'De l\'agilité pour Amayiro.', 'Amayiro vous a demandé vingt anneaux d\'agilité.', '10000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('702', 'Amener les capes.', 'Amayiro vous a demandé d\'apporter 10 capes bontariennes au capitaine Koda,à côté. Elles se confectionnent avec une teinture magique bleue,une plume de corbac et une laine de boufton blanc.', '11400', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('705', '21 heures.', 'Danathor vous a demandé de le retrouver à 21 heures avec les ingrédients indiqués sur sa liste.', '13500', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('708', 'De la viande pour le cuisinier.', 'Le cuisinier de la milice de Bonta,Rei Nisuc,a besoin de 10 cuisses de Tofu.', '13500', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('707', 'Assassinat refusé.', 'Retournez voir Amayiro.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('712', 'Amener le venin.', 'Le capitaine Essacarf,dans la prison de Bonta,a besoin de 30 pods de venin de scorbute.', '15600', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('714', 'Combat... pour rire ?', 'Vous devez trouver Dike Tarak dans le quartier,le défier et le battre.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('713', 'Combat... pour rire.', 'Vous devez trouver Dike Tarak dans le quartier nord,le défier,et perdre.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('716', 'Trouver une faille.', 'Amayiro vous a demandé d\'explorer le Cimetière des Torturés,à l\'Est de Brâkmar,et de voir s\'il y a une faille dans les défenses de la cité. Vous aurez sûrement besoin de matériel particulier.', '19000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('717', 'Travail au choix.', 'Vous devez apporter à Amayiro : soit 20 planches en frêne,soit 10 racines d\'Abraknyde.', '21000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('721', 'Raid chez Brâkmar.', 'Amayiro vous a demandé de vaincre des guerriers brâkmariens et de récupérer dix badges sur eux.', '28000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('723', 'Une leçon à donner.', 'Un certain William Lengland perturbe les habitants de Bonta : il faut aller le calmer,par la force.', '34000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('724', 'Dans les geôles de Brâkmar.', 'Amayiro vous a demandé de sauver Houé Dapyus,dans la prison de Brâkmar,et de le ramener à la milice bontarienne.', '37000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('154', 'Pandrista à gogo !', 'Trouvez un marchand ambulant : vous pourrez lui régler la taxe sur la Pandrista. N\'hésitez pas à insister lourdement.', '1000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('153', 'Y\'a du soleil à Pandala !', 'A votre arrivée dans Pandala,vous êtes invité à vous mettre en règle avec l\'administration : régler la taxe sur la Pandrista s\'impose.', '1000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('155', 'Ma première Shaman Party', 'Allez demander de l\'aide au Shaman de toute urgence !', '4700', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('170', 'Un nouveau message du sage', 'Un messager vous apporte un message,parlez-lui !', '5000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('168', 'Petit entrainement au bouclier', 'Pour vous remercier de la livraison,le forgeur de bouclier vous a fait cadeau d\'un petit bouclier d\'entraînement,Parlez au maitre du Dojo pour un petit entraînement.', '1000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('162', 'L\'accent sur le voyage', 'Le sage Lenald vous demande d\'aller au village de l\'Aerdala...ou de Terrdala,impossible d\'être sûr à cause de son accent. Une fois sur place,parlez au maître Pandawushu.', '750', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('161', 'Chez le forgeur de Boucliers', 'A Terrdala,personne n\'est disponible pour aller porter les ressources à la forge de boucliers. Forger des boucliers est pourtant indispensable aux guerriers Pandawas... Heureusement que vous êtes là !', '750', '0', '7097,1', '0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('165', 'Qui a peur du méchant chacha', 'Poochan recherche son futur mari,les seuls indices qu\'elle a sont qu\'il doit toujours se trouver à Pandala,et qu\'il a peur des chachas. Le plus simple est encore de se transformer en chacha et de sauter dans les bras de tout le monde non ?', '1000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('166', 'Arrangement à l\'amiable', 'La coutume ici veut que le père décide qui son fils ou sa fille épousera. Vous devez donc convaincre le père de vous céder son fils. Il faudra faire tout ce qu\'il faut pour ça.', '1000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('167', 'Conquête des Dento', 'Pour libérer Lanma de ses obligations,il va falloir rompre la promesse de mariage qu\'il a avec une fille du Dojo Dento.', '1000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('205', 'Récompense ?', 'Poochan vous attend.', '7000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('174', 'Balade dans l\'Espace et le Temps d\'Amakna ', 'Cette Balade dans l\'Espace et le Temps d\'Amakna (B.E.T.A.) permettra de sauver le monde ! \n', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('173', 'Un Kouartz et ça repart', 'Amine propose à Henual d\'utiliser le dernier recours :... du Kouartz !', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('172', 'Amine,de rien!', 'Il faut transmettre une lettre à Amine au temple Xelor de la part du surveillant Henual.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('178', 'Boire et déboires', 'Un marchand ambulant vous vendra certainement une de ces boissons fermentées qui ont fait la réputation de l\'île. Il vous suffit d\'en trouver un !', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('185', 'Passage de grade Rokwa', 'Il faut se présenter au maître un samedi pour passer l\'évaluation du grade Rokwa.', '30000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('184', 'Passage de grade Gokwa', 'Il faut se présenter au maître un samedi pour passer l\'évaluation du grade Gokwa.', '30000', '0', ' 0', ' 0', ' 0', '0');
