INSERT INTO `quetes_objectifs` VALUES ('180', 'Passage de grade Ikwa', 'Il faut se présenter au maître un samedi pour passer l\'évaluation du grade Ikwa.', '5000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('187', 'Début entrainement', 'Pour commencer l\'entraînement,il faut obtenir un certificat shamanique ainsi qu\'une tenue Pandawushuka.', '0', ' 0', '7313,1', '0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('182', 'Passage de grade Sankwa', 'Il faut se présenter au maître un samedi pour passer l\'évaluation du grade Sankwa.', '15000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('181', 'Passage de grade Nikwa', 'Il faut se présenter au maître un samedi pour passer l\'évaluation du grade Nikwa.', '10000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('191', 'Yondanwa', 'Il faut se présenter au maître un samedi pour passer l\'évaluation du grade Yondanwa.', '60000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('190', 'Sandanwa', 'Il faut se présenter au maître un samedi pour passer l\'évaluation du grade Sandanwa.', '50000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('186', 'Shodanwa', 'Il faut se présenter au maître un samedi pour passer l\'évaluation du grade Shodanwa.', '30000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('189', 'Nidanwa', 'Il faut se présenter au maître un samedi pour passer l\'évaluation du grade Nidanwa.', '40000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('175', 'Legende Lenalde', 'Enfin ! Vous touchez au but ! Mais vous n\'êtes pas au bout de vos surprises ! Le Lénald de l\'île des Wabbits va vous révéler un grand secret longtemps gardé...', '7500', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('188', 'Dans le temple de votre dieu', 'Suivant les conseils du sage Lenald,vous préparez votre prochaine expédition. Pour cela il vous faut vous rendre dans le temple de votre divinité en Amakna.', '7500', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('176', 'Dark Vlad,un gars qui en a !', 'Purifiez votre âme et lavez-vous les oreilles : le sage Lénald vous révèle la vérité sur le Dofus de Pandala.', '40000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('193', 'Le réceptacle aux Dofus', 'Le Grandapan a accepté le cadeau des Enutrofs avec joie. Fervent admirateur des Dofus,il vous accueille à bras ouverts. Pour satisfaire votre curiosité à propos des Dofus,il vous invite à aller voir le réceptacle aux Dofus.', '1500', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('192', 'Bons baisers d\'Astrub', 'Le shaman est débordé,il n\'a pas de temps à vous consacrer. Entre les Pandikazes,la maladie des Enutrofs d\'Astrub et le reste...Il vous conseille de parler à Pyrite,Enutrof en quarantaine,si vous désirez l\'aider.', '1500', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('194', 'Un tiens vaut mieux que deux tu boiras', 'La voie de l\'eau consiste à résister à... l\'absorption et à l\'immersion (partielle et totale) d\'eau par tous les moyens. Ne jamais en boire,se laver uniquement en cas de nécessité. Trouvez le maître de la voie de l\'Eau : il en sait plus qu\'il n\'y paraît.', '10000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('207', 'Le Grandapan', 'Apporter le faux Dofus au Grandapan.', '2200', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('195', 'Ecuyer,Palefrenier,même combat.', 'Des Brâkmariens ont détruit des greniers bontariens. Il faut  trouver un moyen de ravitailler les montures de la milice en allant trouver une solution auprès de Merlan,à l\'atelier des alchimistes.', '50000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('196', 'Dans la peau d\'un espion.', 'Suite à l\'incendie d\'entrepôts de nourriture par les Brâkmariens,Ebru Of El veut savoir ce que les ennemis manigancent.', '50000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('197', 'Levez-vous et marchez.', 'Elviana Tirips veut interroger les renégats bontariens mais avant de pouvoir le faire il faut les ressusciter.', '50000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('782', 'Agriculture à haut risque.', 'Kaffra a eu vent des plans de Bonta pour accroître sa production céréalière. Il a besoin de 20 Graines de scorbute pour que cette production serve les desseins de Brâkmar.', '50000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('200', 'Brâkmariens bras cassés.', 'Les agents brâkmariens étant des incapables,Kaffra vous a demandé de vous rendre à Astrub et de convaincre le marchand bontarien Raymond Santho de ravitailler Brâkmar.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('783', 'Intoxication alimentaire.', 'Suite à votre rapport,Ledrob vous a confié 20 Graines de scorbute modifiées à déposer dans l\'atelier des alchimistes de Bonta. Il faut les déposer de nuit lorsque Merlan l\'Enchaîneur est endormi.', '50000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('198', 'Inquiétudes brâkmariennes.', 'Ledrob se demande ce qui se trame à Bonta en ce moment. Il vous envoie sur place pour prendre contact avec l\'espion Finche Aticus.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('787', 'J\'ai dit : Lève-toi et marche !', 'La malédiction d\'Erazal vous a appris comment forcer une âme à se réincarner contre sa volonté. Pour cela,il faut créer une potion particulière et suivre un rituel précis.', '50000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('199', 'Lève-toi et marche.', 'Divad ne parvient pas à ressusciter Terra Tibya pour l\'interroger. Il faut donc improviser un sort de résurrection qui en soit capable et c\'est la mission qui vous a été confiée.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('201', 'Trouver le hors-la-loi', 'Rok a été aperçu au Nord de Pandala. \nIl est condamné à l\'emprisonnement à Astrub. \n', '0', ' 6250', '0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('202', 'Trouver le hors-la-loi', 'Musha a été aperçu au sud de Pandala. \nIl est condamné à l\'emprisonnement à Astrub. \n', '0', ' 64000', '0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('203', 'Trouver le hors-la-loi', 'Marzwel a été aperçu du côté d\'Aerdala \nIl est condamné à l\'emprisonnement à Astrub. \n', '0', ' 25000', '0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('206', 'Trouver la hors-la-loi', 'Aermyne a été aperçue du côté de Terrdala. \nElle est condamnée à l\'emprisonnement à Astrub. \n', '0', ' 16000', '0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('785', 'Interro surprise.', 'Elviana Tirips veut savoir quelles informations les renégats bontariens ont données aux Brâkmariens et c\'est vous qui êtes en charge de l\'interrogatoire.', '200000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('786', 'La chasse aux renégats est ouverte.', 'Des renégats bontariens se sont réfugiés à Brâkmar. Vous devez les retrouver et les éliminer puis ramener leur dépouille à Bonta.', '200000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('209', 'Le secret de Terra Tibya.', 'Terra Tibya est enfin ressuscitée. Reste à la persuader de vous révéler son secret.', '200000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('208', 'Le pont vers Terra Tibya.', 'Ledrob Terceséc veut mettre la main sur les statuettes de Sumens. Il vous a chargé de capturer leur gardien.', '200000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('214', 'Parler à Badufron,Chef Mercenaire d\'Astrub', 'Badufron connaît les lois concernant les trafiquants de Dofawa.', '1000', '45', '7929,1', '0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('213', 'Parler au Mage Ax', 'Le Mage Ax doit pouvoir analyser les oeufs de Tofu et le Dofus.', '1000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('212', 'Des oeufs de Tofu pour Nowa', 'Apporter à Nowa : x2 oeufs de Tofu', '1000', '5', '0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('217', 'Rapporter une plume de Piou violet à Acidrik', 'Il en a besoin pour lever la magie d\'Herdegrize sur le plagiat de son livre.', '1500', '100', '7930,1', '0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('216', 'Obtenir le faux livre d\'Acidrik', 'Ce livre se nomme \"le temps des dragons\" et non \"le Temps des Dragons\".', '1000', '200', '0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('215', 'Apporter une bière à Acidrik', 'Il est capable de lire ton avenir dans la mousse d\'une bonne bière.', '500', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('219', 'Se rendre là où l\'Infini et le Néant se rejoignent', 'Ici,tu trouveras une réponse à propos de l\'âme de Ménalt.', '1500', '50', '7934,1', '0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('234', 'Symbole et symbolisme', 'Retournez auprès de Nistracolamus pour découvrir la signification de ce fameux symbole.', '1000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('218', 'Découvrir la statue du dieu Xélor ', 'Il faut retenir le symbole gravé sur le marteau du dieu.', '500', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('220', 'La caverne du Koulosse', 'On raconte que le Koulosse habite cette caverne depuis des lustres. L\'éleveur qui s\'y est aventuré n\'en n\'est jamais revenu...', '0', ' 0', ' 0', ' 0', ' 0', '0');
