INSERT INTO `quetes_objectifs` VALUES ('403', 'Un oeuf tout neuf', 'Vil Smisse vous a demandé de lui rapporter un oeuf de tofu obèse. Encore faut-il trouver le nid...', '5000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('406', 'Tueurs à gage à engager', 'Vil Smisse ne pourra utiliser le blopisier empoisonné. Il se rabat donc sur l\'attaque de front. Pour cela,il veut engager des assassins,il a donc besoin d\'argent.', '76000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('405', 'Un cadeau empoisonné ', 'Vil Smisse a décidé d\'éviter l\'approche directe et de faire les choses plus à la façon traditionnelle des Roublards - sournoisement.', '10000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('407', 'Trouble-fête', 'Les assassins ont échoué à tuer le prince... Vil Smisse vous demande d\'aller engager un Lenald pour le trucider.', '140000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('408', 'Sus au tofu!', 'Vous savez où le pwince d\'Wa est parti chasser,enfin à peu près... c\'est au sud de Bonta en tout cas. Reste à trouver ce fameux pwince.', '100000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('381', 'Au travail', 'Allez demander au contremaître ce que vous devez faire.', '250', '250', '0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('414', 'Servir ou rendre service?', 'L\'intendant San Lavalss vous demande d\'aider aux derniers préparatifs du mariage.', '86000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('418', '1er étape', 'test', '0', ' 0', '8359,10;376,10;8141,10;8141,10;2087,10', '0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('420', 'Un déguisement pour Emeraude', 'Emeraude cherche désespérément une panoplie Koalak.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('421', 'Répondre au questionnaire', 'Le Gardien du Pont de la Mort exige que vous répondiez correctement à ses questions.', '30000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('422', 'Bien préparer son voyage', 'Le Capitaine Kradoc a besoin que vous lui rapportiez plusieurs objets pour qu\'il puisse vous emmener dans son bateau.', '30000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('423', 'Le Coffret Maudit du Flib', 'Le bateau du Capitaine Kradoc s\'est lamentablement échoué sur l\'île des naufragés. Vous devez retrouver le Coffret Maudit du Flib et quelques objets pour réparer le bateau.', '30000', '2500', '0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('424', 'Vous ne passerez pas !', 'Le Chevalier Noir et Rose est fermement décidé à ne pas vous laisser passer. C\'est peut-être le bon moment d\'employer la force.', '45000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('425', 'Une potion pour le professeur', 'Le Professeur Fournesol a besoin d\'une Potion Bulbique pour se remettre au travail.', '30000', '1000', '0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('426', 'Les machines d\'élevage', 'Polka Merer a besoin de machines d\'élevage pour ses créatures.', '30000', '2000', '0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('427', 'Une histoire de farines', 'Paulette Pain-bèche la boulangère a l\'intention de recouper sa farine de Xavier le Boulanger avec différentes farines bon marché. Elle a eu l\'idée,à vous de trouver les farines !', '30000', '1500', '0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('428', 'A la recherche du fils caché ', 'Le Docteur Nauh a perdu son fils dans un laboratoire infesté de terribles créatures sauvages. Vous devez le retrouver et vous assurer qu\'il est encore en vie !', '30000', '1000', '0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('429', 'Le docteur est morose', 'Le pauvre Docteur Morose a perdu sa femme. Aidez-le à la trouver dans la Jungle Sombre et tentez de la persuader à retrouver son mari pour qu\'il puisse reprendre ses recherches.', '30000', '1000', '0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('430', 'L\'équipement amélioré ', 'Spitte Fayeur recherche un casque et une cape de pilote améliorés. Seul un tailleur expérimenté pourra vous aider.', '30000', '4000', '0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('431', 'Deux Dragodindes pour Flip Flap', 'Flip Flap voudrait que vous lui rameniez un certificat de Dragodinde Rousse ainsi qu\'un certificat de Dragodinde Amande.', '30000', '30000', '0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('435', 'Chasse dans la Jungle Sombre', 'Crocodaille Dandi aimerait que vous chassiez pour lui quelques créatures de la Jungle Sombre.', '250000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('433', 'Chasse dans les Plaines', 'Crocodaille Dandi aimerait que vous chassiez pour lui plusieurs créatures des Plaines.', '150000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('432', 'Chasse sur la plage', 'Crocodaille Dandi aimerait que vous éliminiez certaines créatures sur la plage de Corail.', '50000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('436', 'Chasse dans l\'Arbre Hakam', 'Crocodaille Dandi aimerait que vous chassiez pour lui quelques créatures de l\'Arbre Hakam.', '500000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('434', 'Chasse dans la Tourbière sans fond', 'Crocodaille Dandi aimerait que vous chassiez pour lui quelques créatures de la Tourbière sans Fond.', '250000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('437', 'Un exemple pour les Pirates', 'Kito vous a demandé de faire un exemple pour les Pirates qui lui lancent des boulets.', '30000', '1000', '0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('438', 'Le Chevalier Noir et Rose', 'Peggy la Porkass aimerait que vous affrontiez le terrible Chevalier Noir et Rose à sa place.', '30000', '2000', '0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('440', 'L\'échange maudit', 'Farle Ingalsse est sûrement la seule personne d\'Amakna capable de rendre utilisable cette épée maudite...', '0', ' 0', '8992,1', '0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('456', 'Vite,vite !', 'Milone serait apparemment sur le point d\'être cambriolé. Il faut vite aller empêcher cela.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('469', 'Mauvais hameçon ?', 'Le voyage s\'est passé sans encombre. Les bandits n\'ont pas attaqué... Amayiro attend votre rapport.', '75000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('468', 'Piège pour Ecaflips.', 'Amayiro veut tendre une embuscade aux bandits,pour cela il a besoin d\'un bijou de grande valeur afin de les attirer dans son piège.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('466', 'Escorte Gueurle.', 'Votre mission est d\'escorter la troupe de marchands de Gueurle jusqu\'à Astrub.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('470', 'Au rapport.', 'Le convoi a été attaqué par des bandits Ecaflips semblables à ceux dont vous avez découvert le campement. Amayiro attend votre rapport pour convaincre le conseil d\'agir.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('473', 'Une bonne Rigalade', 'Rigal Main Crochue,au temple Sram,est réputé pour détenir de nombreuses informations,mais toute information à un prix. Reste à savoir à combien s\'élève la vôtre.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('474', 'Il est frais mon poison.', 'Vous devez trouver les réserves de poison de Brâkmar et dérober le registre des clients de l\'atelier des alchimistes. Bishom Pot pourra peut-être vous aider.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('923', 'L\'alchimie pour les nuls.', 'Vous devez voler le registre des clients dans l\'atelier des alchimistes de Brâkmar et l\'apporter à Rigal Main Crochue.', '90000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('922', 'Complicité accrue.', 'Afin de pénétrer dans les réserves de poison de Brâkmar,vous aurez besoin des services d\'un Sram ou d\'un espion qualifié.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('478', 'Attrape-nigaud.', 'Un rendez-vous entre un Brâkmarien et le Bontarien corrompu va avoir lieu à la tour de Bonta. Il faut les capturer tous les deux si possible.', '105000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('925', 'Pas d\'issue pour les traîtres.', 'Vous devez faire le tour des miliciens pour les poster à la Tour de Bonta.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('930', 'Bwork,quelle odeur !', 'Soit Epuop Tik n\'en sait pas plus,soit il refuse de vous en dire davantage. Quoiqu\'il en soit,les bworks mages devraient être moins réticents à vous informer sur l\'un des leurs.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('504', 'Danathor,qu\'est-ce que tu nous mijotes encore ?', 'Danathor semble avoir une nouvelle offre à vous faire. Probablement quelque chose de louche.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('932', 'La poupée enchantée.', 'Vous devez trouver quatre Sadidas,suffisamment expérimentés pour invoquer des Gonflables,pour un rituel dans les eaux enchantées des lacs Koalaks.', '146000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('931', 'Génialissime.', 'Si vous levez la malédiction pesant sur Jibworknouille,il vous divulguera sûrement ce qu\'il sait en signe de reconnaissance. Il a suggéré de se renseigner dans une bibliothèque.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('794', 'A la recherche du greffier attristé.', 'Aigurraf,Greffier de l\'Ordre de l\'Œil Attentif,écume les tavernes depuis la disparition de sa souris. Il faut le ramener auprès d\'Ebru Of El.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('767', 'Sortie de prison.', 'Vous devez sauver un guerrier qui a été capturé par Bonta : il se trouve dans la prison de la milice bontarienne,au sous-sol.', '40000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('951', 'Ennemi intime.', 'Trouver et éliminer votre cible.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1051', 'Gagner le combat.', 'Continuez d\'attaquer votre cible pour gagner le combat. Passez votre tour grâce au bouton {ui,banner,btn_skipTurn::fin de tour} lorsque vous voulez regagner vos {ui,banner,lbl_pa,2::PA} et vos {ui,banner,lbl_pm,2::PM}.', '5', '0', '10784,1', '0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1061', 'Terminer le combat.', 'Gagnez votre combat contre le monstre. Attention,les tours de jeu ont désormais une durée limitée. La {ui,banner,tx_artwork,3::jauge de temps restant} indique combien de temps il vous reste avant que votre tour ne se termine.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1042', 'Déplacement sur la carte.', 'Bienvenue dans le tutorial guidé. Cliquez avec le bouton gauche de votre souris autour de votre personnage pour le déplacer. Si vous cliquez loin de votre personnage il courra,sinon il marchera.', '5', '0', '10792,1', '0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1050', 'Passer son tour.', 'Lorsque vous avez terminé vos actions,cliquez sur le bouton {ui,banner,btn_skipTurn::fin de tour} avec le bouton gauche de votre souris pour accélérer le déroulement du combat et regagner vos {ui,banner,lbl_pa,2::PA} et vos {ui,banner,lbl_pm,2::PM}.', '5', '0', '10799,1', '0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1044', 'Equiper un objet.', 'Ouvrez votre interface de {ui,banner,btn_character,1::caractéristiques}. Sélectionnez l\'{ui,storage,grid::Anneau du débutant} que vous avez gagné en maintenant enfoncé le bouton gauche de votre souris et déposez-le  dans l\'{ui,characterSheetUi,slot_2,2::emplacement réservé à l\'anneau} de votre fiche de personnage. \nVous pouvez également l\'équiper en effectuant un double clic avec le bouton gauche de votre souris sur l\'{ui,storage,grid::Anneau du débutant}.', '5', '0', '10793,1', '0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1060', 'Equiper ses objets.', 'Ouvrez l\'interface des {ui,banner,btn_character,1::caractéristiques} et équipez sur votre personnage les objets que vous avez gagné.', '0', ' 0', '10793,1', '0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1048', 'Se déplacer en combat.', 'Votre personnage possède des {ui,banner,lbl_pm,2::points de mouvement} (PM) pour se déplacer. Se déplacer d\'une case coûte 1 PM. Déplacez votre personnage en cliquant sur une case avec le bouton gauche de votre souris. Vous récupérez vos {ui,banner,lbl_pm,2::PM} au début de votre prochain tour.', '5', '0', '10797,1', '0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1049', 'Utiliser un sort.', 'Sélectionnez {ui,banner,gd_spellitemotes,0::un sort} et cliquez avec le bouton gauche de votre souris sur une cible. Certains sorts se lancent sur les ennemis,et d\'autres sur les alliés ou sur votre personnage. La portée du sort est indiquée grâce aux cases bleues. Les sorts nécessitent des {ui,banner,lbl_pa,2::points d\'action} (PA) pour être lancés. Vous récupérez vos {ui,banner,lbl_pa,2::PA} au début de votre prochain tour.', '5', '0', '10798,1', '0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1047', 'Choisir sa position de départ.', 'Cliquez sur une des {cell,272::cases bleues} avec le bouton gauche de votre souris pour choisir votre position de départ et cliquez sur le {ui,banner,btn_ready::bouton prêt} pour commencer le combat.', '5', '0', '10796,1', '0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1045', 'Changer de carte.', 'Pour changer de carte,déplacez votre curseur sur un des {arrow,1280,520,0,0::bords de l\'écran} et cliquez avec le bouton gauche de votre souris lorsque le curseur se transforme en flèche.', '5', '0', '10794,1', '0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1053', 'Vaincre le monstre', 'Pour continuer votre quête,vous devez commencer par aller dans la {arrow,700,870,0,1::salle suivante} et lancer un combat contre {monster,2781::le monstre}.', '5', '0', '10801,1', '0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1059', 'Conclure l\'apprentissage.', 'Pour valider votre quête,vous devez parler à {npc,1222::Maître Pifo}.', '5', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1052', 'Commencer une quête.', 'Parlez à {npc,1222::Maître Pifo} pour lancer une quête (les PNJ qui proposent des quêtes ont un point d\'exclamation au dessus de leur tête).', '5', '0', '10800,1', '0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1043', 'Parler à un pnj.', 'Cliquez sur {npc,1222::Maître Pifo} avec le bouton gauche de votre souris et sélectionnez l\'action \"Parler\" pour dialoguer avec lui.', '5', '0', '10785,1', '0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1046', 'Déclencher un combat.', 'Cliquez sur {monster,2785::Epouvantail d\'Incarnam} avec le bouton gauche de votre souris pour lancer un combat.', '5', '0', '10795,1', '0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1057', 'Une vie compliquée.', 'Vous auriez dû suivre les conseils de Sel Dawa et vous méfier. A présent,il semblerait qu\'on veuille votre peau. Hélas,la vie n\'est pas aussi simple qu\'on voudrait le croire.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1058', 'On en viendra tous à bout.', 'Grâce à votre aide,Sel Dawa est en mesure d\'éradiquer le mal qui frappe le marché des boissons pandawas,à condition de trouver un antidote.', '3000', '0', '10818,1', '0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1056', 'La vie facile.', 'A peine arrivé,on vous propose déjà un moyen de gagner votre vie facilement et sans trop d\'effort. Que demander de plus ?', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1091', 'Usine à jouets.', 'D\'abominables créatures ont cambriolé l\'entrepôt du Père Nowel. Vous devez récupérer les caisses de cadeaux qu\'elles ont volées,avec ou sans leur consentement,et les apporter à Félix Nowel.', '0', ' 1000', '10920,1', '0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1054', 'Ne pas traduire', 'Ne pas traduire ! Quête utilisée pour faire apparaître un point d\'exclamation en permanence au-dessus du PNJ Fée Risette.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('996', 'Se rendre au temple.', 'Allez au temple de votre classe,profitez-en pour combattre le dopeul qui s\'y trouve.', '5000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('884', 'Réengagez-vous,qu\'il disait.', 'Snori Nairb a promis de partager ses kamas et son expérience avec vous si vous vous engagez dans la milice de Bonta.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('64', 'Je veux tout tout de suite', 'Devenir le roi du monde', '500', '1000', '968,1;948,5', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('65', 'Je veux plus rien !', 'Je veux moins d\'xp et moins de kamas', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('766', 'L\'attaque bontarienne.', 'Vous devez vous rendre au rempart Est et y vaincre les Bontariens.', '37000', '0', ' 0', ' 0', ' 0', '0');
