INSERT INTO `quetes_objectifs` VALUES ('475', 'Affaire louche.', 'Vous allez profiter de l\'absence de Xarthan pour vous introduire discrètement dans sa maison et la fouiller. Les services d\'un bricoleur compétent seront nécessaires.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('924', 'A la tour.', 'Xarthan a rendez-vous avec un Brâkmarien dans la Tour de Bonta après la réunion des notables. Il vaut mieux vous dépêcher pour ne pas le rater.', '95000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('534', 'Le choix.', 'Vous avez trouvé l\'espion à temps. Mais allez-vous suivre les ordres et récupérer les documents en le laissant là ?', '205000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('144', '... ou crocs de Xelors.', 'Les Xélors semblent intéressés. D\'après Ant,ce serait pour mettre des aiguilles sur leurs sabliers... enfin il n\'est pas sûr. ', '3000', '500', '0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('115', 'Trouver le hors-la-loi', 'Certains disent avoir vu Qil Bil vers les plaines de Sidimote. \nQil Bil est recherché pour arrachage de membres. \nIl est condamné à l\'emprisonnement à Astrub. \n', '0', ' 19200', '0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('883', 'Réengagez-vous,qu\'il disait.', 'Snori Nairb a promis de partager ses kamas et son expérience avec vous si vous vous engagez dans la milice de Brâkmar.', '0', ' 0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('32', 'Qui sème le vhan,récolte l\'Artempeth', 'Pour tester sa théorie sur l\'apparition des fleurs carnivores,Trapchet demande de semer des graines de rose obscures,puis de les récolter...', '15000', '2500', '0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('5', 'L\'aphone et la flore', 'Trois-Fleurs,un ami de Trapchet réside à Amakna et connait parfaitement la flore de sa région. Il faut lui demander des renseignements sur les fleurs agressives qui s\'y trouvent.', '12500', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('3', 'L\'epyss s\'enlise', 'Erty Trapchet veut étudier des graines de pissenlit,pour les comparer avec de l\'Epyss...', '6000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('2', 'L\'arroseur arrosant ', 'Erty Trapchet a oublié de s\'occuper de ses plantes,il faut se rendre à son domicile pour les arroser.', '3000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('4', 'Mais quel herbier !', 'Pour ses recherches,Trapchet a besoin d\'étudier la répartition géographique des fleurs aux alentours d\'Astrub.', '6500', '500', '0', ' 0', ' 0', '0');
