INSERT INTO `quetes_objectifs` VALUES ('143', 'Chasse sauvage', 'De sombres créatures ruinent les affaires d\'Ant Emicheun,s\'en débarasser lui semble être une solution... du moins en attendant de comprendre ce qu\'elles font ici...', '5000', '750', '0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('141', 'Les oeufs peuvent-ils nager ?', 'Plus d\'agent infectieux dans les plumes de Tofus malades. Par contre leurs oeufs en seraient probablement une piste pour les Eniripsa. Encore des Kamas à se faire !', '2500', '375', '0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('21', 'Ca va saigner !', 'Il vous prend le besoin d\'en savoir plus sur vous-même et vous décidez d\'aller dans un endroit sanglant... la boucherie.', '600', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('25', 'Ca sang le roussis !', 'Votre maître a entendu qu\'un mineur a récemment été changé en mineur sombre. Il veut en avoir l\'artère net !', '1200', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('41', 'Stupides Enutrofs !', 'Les Enutrof ont découvert quelque chose,ils auront besoin de toi.', '1200', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('46', 'Extrait de Dzibi', 'La fleur telle quelle ne conviendra pas,il vous faut une infusion !', '1200', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('47', 'Livraison express', 'Vous devez rapporter l\'infusion à votre maîtresse de guilde pour qu\'elle puisse fabriquer votre bâton ! \nSi au passage vous avez le temps et les moyens d\'aller apporter de l\'infusion à votre fournisseur de fleur,cela lui fera grandement plaisir.', '1200', '0', '6781,1', '0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('44', 'Fleur de Dzibi', 'Pour que votre maîtresse de classe puisse vous confectionner un bâton digne de ce nom,vous devez trouver de la fleur de Dizbi.', '600', '0', '6798,1', '0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('118', 'On a faim !', 'Les bouftous d\'Allisteria ont faim. Il va falloir tous les nourrir !', '600', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('127', 'Boudin Bouilli de Bouftou', 'Sauver la tradition du bon Boudin Bouilli de Bouftou', '1200', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('132', 'Bats toi si t\'es une Feca', 'Le célèbre Fallanster vous a demandé d\'entraîner sa fille au combat.', '1200', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('160', 'Un tiens vaut mieux que deux tu boiras', 'La voie de l\'eau consiste à résister à... l\'absorption et à l\'immersion (partielle et totale) d\'eau par tous les moyens. Ne jamais en boire,se laver uniquement en cas de nécessité. Trouvez le maître de la voie de l\'Eau : il en sait plus qu\'il n\'y paraît', '9000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('159', 'Le sage Lenald', 'Une vendeuse de rafraîchissement Pandawaïenne vous conseille de rencontrer le sage Lénald. Selon elle,lui seul détient la vérité sur Pandala et ses secrets.', '1000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('157', 'La statue de la déesse', 'Le Grandapan a accepté le cadeau des Enutrofs avec joie. Fervent admirateur des chercheurs de Dofus,il vous accueille à bras ouverts.', '750', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('156', 'Le Grandapan : Un pandawa sympa', 'Les Enutrofs en cure chez le Shaman vous conseillent d\'aller voir le Grandapan. Très au fait des coutumes locales,il est aussi un fin connaisseur des légendes Pandawaïennes.', '500', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('158', 'Libations pour la déesse', 'Grâce à vous,l\'Assistant du Shaman peut commencer son rituel de remerciement à la déesse. Il sera fin prêt dès que vous lui aurez remis une offrande pandawaïenne traditionnelle.', '750', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('259', 'Au service du roi', 'Le roi aime monter,il faut lui trouver des dragodindes', '75000', '0', ' 0', ' 0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1153', 'Vétérans mansots.', 'Une nouvelle vague de mansots attaque les pêcheurs. Vous devez aider Mel à les repousser.', '669000', '10000', '11196,1', '0', ' 0', '0');
INSERT INTO `quetes_objectifs` VALUES ('1152', 'Guerriers mansots.', 'Une nouvelle vague de mansots attaque les pêcheurs. Vous devez aider Mel à la repousser.', '446000', '5000', '11196,1', '0', ' 0', '0');
